INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 245, 'Global community seedbanks platform', 'Bioversity International, in collaboration with a number of research and development organizations from Austria, Brazil, China, Nepal, Peru, the Netherlands and Zimbabwe, established a global community seedbanks platform to facilitate connections among community seedbanks and community seedbank networks, scale out their activities and achievements and contribute to their sustainability. The platform will provide technical and organizational support, collect/share basic data; compile/share practical experiences and successes; support national and international policy development; and promote national networking.', 2018, 'https://www.bioversityinternational.org/news/detail/a-global-community-seedbank-platform/', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=245&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 246, 'New method for GHG measurements with closed chambers at night time', 'The standard closed chamber method for GHG measurements in rice has certain limitations (e.g. time, number of chambers, logistics). The newly proposed variation of this method with gas sampling being conducted at nighttime more chambers can be used at the same time over a longer closure duration. An analysis of a large Eddy Covariance data set showed a linear relationship between nocturnal fluxes and the full 24-hour fluxes.', 2018, 'https://doi.org/10.1371/journal. pone.0191352', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=246&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 250, 'Food security and drought monitoring and early warning tool considering local vulnerabilities ', 'Digital platform for food security and drought monitoring and evaluation at the community level, integrating mobile data collection, estimation of a municipal acute food insecurity index, data management, analysis and dissemination. ', 2018, 'https://github.com/BioversityCostaRica/SesanV1    http://190.111.0.168/', 1, 'Guatemala', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=250&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 251, 'Out-yield hybrids selected for the three agricultural environments of Mexico: lowland tropical, subtropical and highlands.', '47 hybrids were selected due to their competitive yield and favorable agronomic characteristics in comparison with commercial-testers (three-to-nine evaluation sites). In 2019, yield-validation-trials will be carried out in more than 40 locations. In 2020, the competitive hybrids from the validation-trials will be able to be released to the project seed-companies.', 2018, ' https://masagro.mx/es/2012-06-21-17-47-58/documentos', 47, 'Mexico', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=251&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 252, '5 hybrids of high yield potential for the seed sector of Mexico', 'Five hybrids were selected to be released to the project (MasAgro Maize) seed companies. These hybrids are competitive in terms of yield and agronomic characteristics compared to commercial testers. The hybrids to be released are two for lowland tropical, two for subtropical and one for the highlands of MexicoÂ´s mega-environments.', 2018, 'https://masagro.mx/es/2012-06-21-17-47-58/documentos', 5, 'Mexico', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=252&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 253, '4 new out-yield hybrids of CIMMYT in the Mexican maize seed market.', 'The MasAgro-Maize project has released more than 40 maize hybrids and varieties to the Mexican seed sector . In 2018, 41 CIMMYT hybrids and varieties were produced and sold by the MasAgro-Maize seed companies, four hybrids, which were integrated for the first time into the Mexican maize seed market.', 2018, 'https://masagro.mx/es/2012-06-21-17-47-58/documentos', 4, 'Mexico', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=253&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 254, ' improved multiple stress tolerant varieties that effectively address emerging and future production', ' lines from which hybrids were released: MH50STR, MH51STR , MH52STR, AMANA-1 , AMANA-2, ACR06 TZL COMP3 C4  ,AFLATOXIN SYN2-YF2, PVA SYN 6,PVA SYN 13, TZL COMP4 C4  , Obatanpa/TZLCOMP1SYNW-1/TZLCOMP1SYNW-1,((TZL COMP1-W C6*2/(White DT STR Syn))-DT C1,MAQPMSYN-1,MASYN-VAR4 , MASYN-VAR5 ,CRI Apraku, ((TZL COMP1-W C6*2/(White DT STR Syn))-DT C1, EVDT 99-QPM , TZEE-W\r\n\r\n', 2018, 'https://public.tableau.com/profile/jennifer2749#!/vizhome/MAIZEVarietyReleaseMap2018FinalJJ/Dashboard1?publish=yes', 18, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=254&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 261, 'Innovation platforms for Climate Smart Agriculture in Honduras', 'CSA innovation platform is an institutional innovation to facilitate cross learning and coordination for innovation testing and implementation', 2018, 'https://ccafs.cgiar.org/publications/innovation-platforms-climate-smart-agriculture-honduras#.XHcyDbh7lEY', 1, 'Honduras', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=261&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 263, 'Demonstrated impact of food-assisted maternal and child health and nutrition program on maternal and child nutritional status in Guatemala and Burundi', 'Food-assisted maternal and child health and nutrition (FA-MCHN) programs are widely used to address undernutrition, but little is known about their effectiveness in improving child linear growth. Using a robust cluster randomized controlled study design, this evaluation provides insights on how to improve the nutritional status of children in the worst-off households in vulnerable contexts, such as Guatemala and Burundi.', 2018, 'https://doi.org/10.1093/jn/nxx063; https://doi.org/10.3945/jn.115.227462; https://doi.org/10.1093/jn/nxy138', 1, 'Burundi,Guatemala', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=263&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 268, 'Gender-differentiated indicator of nominal rate of protection - a methodology to understand the differentiated impacts of policies on women and men', 'Household level surveys (World Bank LSMS) and data from the Ag Incentives Consortium were used to compute national gender-differentiated nominal rates  of protection (NRPs). This new methodology brings a gender lens to our understanding of agricultural policies and can help policy makers understand the differentiated impacts of policies on women and men.', 2018, 'https://www.slideshare.net/IFPRI-PIM/agricultural-price-incentives-towards-genderdifferentiated-indicators', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=268&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 271, 'Dataset on yield changes due to climate change based on Agricultural Model intercomparison and improvement project (AgMIP) Global Gridded Crop Model Intercomparison', 'New methodology that merged together the most useful and accurate results from the AgMIP (Agricultural Model intercomparison and improvement project) GGCMI (Global Gridded Crop Model Intercomparison) crop model comparison for the world.\r\n', 2018, 'http://ebrary.ifpri.org/cdm/ref/collection/p15738coll2/id/132940', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=271&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 272, 'Decision-making tool for national implementation of the Plant Treatys multilateral system of access and benefit-sharing', 'The decision making tool is designed to assist policy actors to identify appropriate measures to implement the Plant Treatyâs multilateral system of access and benefit-sharing. The decision-making tool is divided into the eleven sections corresponding to issues that national-level policy actors need to address. Each section includes draft âbaselineâ legal provisions related to each option; these can be adapted/incorporated into new laws and administrative guidelines. \r\n ', 2018, 'https://cgspace.cgiar.org/handle/10568/93396', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=272&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 274, 'Using roof-top rainwater harvesting system (RWHS) to irrigate home-based vegetable gardens in Laos', 'A model combining a rainwater harvesting systems (RWHS) water balance and a crop water balance was developed to simulate various scenarios of cropping cycles irrigated by a RWHS with varying dimensions. This model ran at a daily time step aimed to assess how the roof and vegetable garden areas and tank capacity influence crop yields, for different cropping calendars and rainfall years.', 2018, 'https://cgspace.cgiar.org/handle/10568/100100', 1, 'Lao PDR', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=274&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 275, 'Climate-Related Risk Maps and Adaptation Plans (Climate Smart MAP) for Rice Production in Vietnamâs Mekong River Delta ', 'The climate-related risks maps and adaptation plans (Climate Smart MAP/CS-MAP) is a participatory approach involving experts from various local and national offices for (1) identifying climate-related risks; (2) delineating affected areas and risk levels; (3) proposing corresponding adaptive plans; (4) fine-tuning and verifying propose measures; and (5) developing integrated provincial and regional adaptation plans. Risk maps were developed for normal and ENSO years by using technical data, infrastructures, and local observations. ', 2018, '', 1, 'Vietnam', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=275&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 277, 'Joint village land use planning in Tanzania', 'Joint village land use planning (JVLUP) allows villages to plan land use collectively to share resources such as grazing and water. It is led by village authorities, with technical support from district government participatory land use management team (PLUM). ILRI and partners are supporting the piloting and upscaling. It is a key stepping stone to collective/group land titles, and rangeland management planning. It resolves land use conflicts between pastoralists and farmers through consultation, negotiation, agreement.', 2018, 'https://cgspace.cgiar.org/handle/10568/79796', 1, 'United Republic of Tanzania', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=277&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 279, 'Woreda Participatory Land Use Planning for Pastoral Areas in Ethiopia', 'Woreda Participatory Land Use Planning (WPLUP) for Pastoral Areas is a process undertaken by district-level government experts working with local land users, to develop a land use plan for better managing and protecting rangelands, and for woreda development planning. \'Joint\' WPLUP has also been introduced to prevent rangeland fragmentation, learning from joint village land use planning in Tanzania. A drylands-specific land capability classification is included. ILRI provided significant technical support to the development of WPLUP.', 2018, ' https://cgspace.cgiar.org/handle/10568/99262', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=279&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 282, '28 new pre-breeding wheat lines with high yield potential and climate resilience for Mexico\'s growing regions.', '28 outstanding wheat lines identified with physiological traits conferring high yields and climate resilience for Mexicoâs growing regions.', 2018, 'https://www.dropbox.com/s/jq5zfwez2lfmplv/Reporte%20MasAgro%20Final%20Meta%204.1.docx?dl=0', 28, 'Mexico', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=282&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 283, 'Seven bread and five durum wheat lines selected in collaboration with INIFAP are variety release candidates.', 'Seven bread and five durum wheat lines selected in collaboration with INIFAP are variety release candidates.', 2018, 'https://www.dropbox.com/s/y40eol0pbro6z9e/Reporte%20MasAgro%20Final%20Meta%204.2.docx?dl=0', 12, 'Mexico', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=283&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 284, '10 new wheat varieties multiplied in collaboration with seed producers located in strategic growing areas of Mexico.', '10 new wheat varieties multiplied in collaboration with Mexican seed producers located in strategic growing areas. The multiplied seed (more than 85 tons) was distributed to farmers in the states of Baja California, Chihuahua, Guanajuato, Hidalgo, Nuevo LeÃ³n, Sinaloa and Sonora.', 2018, 'https://www.dropbox.com/s/jg186ef0j3hzchj/Reporte%20MasAgro%20Final%20Meta%204.3.docx?dl=0', 10, 'Mexico', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=284&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 285, '30 CIMMYT advanced lines with high yield potential, good grain quality and disease resistance selected for national evaluation.', '30 CIMMYT advanced lines with high yield potential, good grain quality and disease resistance selected for INIFAPâs nation-wide wheat yield trials in Mexico.', 2018, 'https://www.dropbox.com/s/jq5zfwez2lfmplv/Reporte%20MasAgro%20Final%20Meta%204.1.docx?dl=0', 30, 'Mexico', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=285&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 286, 'Validation of RUMINANT model of enteric methane emissions', 'RUMINANT is a model that simulates the digestive and metabolic processes of ruminant livestock. Based on the characteristics of the animal and the quality of the feed available (which must be entered by the model user), RUMINANT models nutritional needs and fermentation kinetics and estimates potential consumption, production of milk and meat, manure, excretion of nitrogen, and methane emissions.', 2018, 'https://hdl.handle.net/10568/97097', 1, 'Colombia', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=286&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 289, 'Local Technical Agroclimatic Committees (LTACs) approach generating climate forecasts and crop response', 'The LTAC approach generates weather and climate-forecasts and likely crop response considering such forecast. These forecasts are tailored to local conditions and needs. The forecasts, produced by local agricultural sector institutions and technicians, are used as an input for monthly meetings, where growers and key-stakeholders discuss and analyze such information supporting the decision-making process. LTAC basic premise is that knowledge-intensive practices require learning through interaction and shared understandings, rather than through one-way directed knowledge transfer.', 2018, '', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=289&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 292, 'Implementation of a test to analyze PPO activity (PPO activity controls the darkening of flours and wheat products)', 'A new high throughput, low cost method, to analyze PPO activity was tested and implemented in the lab. In 2018, all BW lines from breeding program were analyzed for this important trait and selection for it can be done by the breeders.', 2018, 'http://hdl.handle.net/11529/10548173', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=292&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 296, 'foresight into changing consumption patterns and implications for research', 'The foresight, ex-ante impact assessment and targeting team conducts research into changing diets and food systems in order to inform CIMMYTs technology development research.  key findings of this research related to South Asia were published\r\nMottaleb, et al. 2018. Changing Food Consumption of Households in Developing Countries: A Bangladesh Case. . DOI: 10.1080/08974438.2017.1402727 and Mottaleb, et al. 2018. Evolving food consumption patterns of rural and urban households in developing countries: A Bangladesh case DOI: 10.1108/BFJ-12-2016-0620', 2018, 'https://www.cimmyt.org/news/city-dwellers-in-africa-and-asia-increasingly-choose-wheat-research-shows/', 1, 'Bangladesh', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=296&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 298, 'Tool to integrate and measure gender equality in monitoring and evaluation of climate services', 'This tool identifies key lessons learned on gender-aware M&E as well as methods to measure empowerment, including use of mixed methods and participatory tools, and gender-aware interview practices. It identifies the data that represent key factors underlying gender inequalities in access and use of weather and climate information and the dataset required to assess participation in agricultural decision-making. Sample quantitative and qualitative questions for collection of the datasets are provided. ', 2018, 'https://cgspace.cgiar.org/handle/10568/99021', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=298&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 299, 'Climate Smart Agriculture investment plans', 'Working closely with the World Bank the Adaptation of African Agriculture Initiative and country partners, this project developed a novel approach for creating CSA Investment Plans at the national level. This innovative approach is based on a foundation of strong stakeholder engagement, combining policy/literature review, key informant interviews, climate risk modeling, foresight analysis, Bayesian cost-benefit analysis, among other approaches to develop a CSA investment plan ', 2018, 'https://drive.google.com/open?id=11UGR5FDsoxu2T7owEk55x7TeB6gV6qbD', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=299&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 307, 'Educator incentive system reduces dropout rates in primary school in Uganda', 'Teachers responded to this Pay for Percentile incentive system in ways that raised attendance rates two school years later from .56 to .60. These attendance gains were driven primarily by outcomes in treatment schools that provide textbooks for grade six math students. We document several results that are consistent with the hypothesis that teacher effort and books are complementary to achieve good education outcomes.', 2018, 'https://www.nber.org/papers/w24911', 1, 'Uganda', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=307&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 308, 'Understanding the impacts of the Yemen Social Fund for Development Cash for Nutrition program on child nutrition and health', 'The impact evaluation found that cash transfers with soft conditionality on attending nutritional training sessions increased child and maternal dietary diversity, maternal knowledge of recommended infant and young child feeding practices, rate of exclusive breastfeeding and early initiation of breastfeeding, treatment of water, and weight-for-height and height-for-age for chilren in the poorest tercile of treated households, whereas they decreased the rate of children being identified as malnourished.', 2018, 'https://www.dropbox.com/s/ulw75bu7ces75hc/YemenCCT_ImpactEvaluationReport.docx?dl=0', 1, 'Yemen', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=308&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 310, 'Estimating minimum nutrient (N,P,K) requirements for climate-smart intensification of maize cropping', 'Building upon the innovation of 2017 for minimum N requirements, we now extended the approach to minimum N, P and K requirements for defined target yields of maize crops. Results are publicly available on www.yieldgap.org and methods and applications have been published (Global Food Security). Both IFA and Yara are very positive about this innovation and use it in their work in SSA to define nutrient requirements for climate-smart intensification. See Evidence and Outcome.', 2018, '', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=310&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 311, 'Framework of analysis of country-level mitigation potential from agricultural sector', 'We developed a generalizable analytical framework for determining country-level mitigation potential from agricultural sector. Used this framework in India to determine country-level mitigation potential together with its spatial distribution using a bottom-up approach. \r\nWe are using the same framework to analyze mitigation potential from agricultural sector in Bangladesh and Mexico in 2019.  ', 2018, '10.1016/J.SCITOTENV.2018.11.225;  https://india.mongabay.com/2019/02/reducing-indias-agricultural-carbon-footprint-with-the-least-costs/_; â¢	https://www.cimmyt.org/press_release/new-study-india-could-cut-nearly-18-of-agricultural-greenhouse-gas-emissions-through-cost-saving-farming-practices/', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=311&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 317, 'Valuation of the general public\'s willingness to pay for the ecosystem services generated by on farm conservation of quinoa agrobiodiversity', 'A choice experiment survey tool explored the extent to which the public is willing to compensate producers for providing ecosystem services by maintaining quinoa agrobiodiversity on farm. For a quinoa conservation program, the willingness to pay was USD 31.79 per respondent, totaling $5.89 million per year - higher than conservation costs ($1.08 million per year). The findings will be used to justify allocation of public funds to conservation of crop genetic resource diversity.', 2018, 'https://repository.usfca.edu/thes/1056/', 1, 'Peru', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=317&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 319, 'Community seedbanks as platforms for building socio-ecological resilience', 'Studies in Guatemala and Nepal showed that the benefits of community seedbanks go beyond seed conservation. Community seedbanks were found to strengthen seed exchanges and contribute to broadening the agrobiodiversity base, allowing to test new varieties and recover local varieties that had disappeared from the community. These changes contribute to socio-ecological resilience. ', 2018, 'https://www.bioversityinternational.org/fileadmin/user_upload/Brief_A_Porcuna_2018.pdf; http://himalayancrops.org/wp-content/uploads/2017/08/GomezCesar_Thesis_Final_Revised.pdf', 1, 'Guatemala,Nepal', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=319&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 321, 'Multistakeholder dialogue tool for cross-border integrated landscape management', 'Conflicts over natural resource management intertwined with insecurity and lack of capacity make it difficult for local governments at the border between Kenya and Somalia to engage in a constructive dialogue. The multistakeholder dialogue tool provides a platform for exchange of information between stakeholders, with the aim of conserving biodiversity resources in the Tana-Kipini-Badana bushland and seascapes of Kenya and Somalia.', 2018, 'http://www.worldagroforestry.org/downloads/Publications/PDFS/PB18002.pdf', 1, 'Kenya,Somalia', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=321&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 323, 'Hybrids offcially released/registered for commecialization ', 'Out of total 68 hybrids (derived_from_following_lines: CIM17MHS02 , CIM17MHS16,_CAH1418, S99TLYQ-B, Across_9331 RE, Across 9942 x Across 9944) licenced_to the_partners, nine_hybrids released; a total 73.7 tons_of_certified_seed was produced. This includes two_hybrids released by BARI, Bangladesh sublicensed to BRAC, two hybrids released by NMRP, Nepal and sublicensed to two_seed companies (SEAN & GATE, Nepal), one_hybrid released by UAS, Raichur & sublicensed to_MAHYCO, India and one hybrid each commercialized by_BAU, Sabor,_Lall Seeds, Srimad Seeds and Kumar Bioseed, India.', 2018, 'https://htma.cimmyt.org/, https://public.tableau.com/profile/jennifer2749#!/vizhome/MAIZEVarietyReleaseMap2018FinalJJ/Dashboard1?publish=yes', 68, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=323&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 324, 'Environmental suitability maps for Ethiopian sheep and goat', 'These maps will allow to disseminate improved small ruminant genotypes (outputs from small ruminant community based breeding programmes) in relevant agro-ecologies.', 2018, 'http://repo.mel.cgiar.org/handle/20.500.11766/9839', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=324&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 325, 'Three CIMMYT Maize Lines (CML) released ', 'Three stress-resilient CIMMYT Maize Lines (CML), including CML578, CML-579 and CML-580 globally released as international public good, and available from gene back of CIMMYT for use in maize breeding programs in tropics.', 2018, 'https://www.dropbox.com/s/c155sc6mf92ghuw/CML%20announcement_catalog%20_2018_final.docx?dl=0', 3, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=325&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 327, 'Field manual for drought phenotyping', 'A protocol for quantitative management of drought stress in field phenotyping developed and published. ', 2018, 'https://www.cimmyt.org/tag/new-publications/', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=327&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 328, 'Sanility tolerant hybrids', 'A set of five promising salinity tolerant hybrids identified, and taken forward for large-scale on-farm testing by partners in Bangladesh (BARI and BRAC) in salt-affected areas in South Bangladesh.', 2018, 'Not weblink', 5, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=328&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 330, 'Stakeholders Prioritization Framework of Climate-Smart Agriculture Interventions ', 'Evaluates stakeholdersâ prioritization methodology for investment decision making on climate smart agriculture technologies, practices and services at the sub-national level;\r\n\r\nMethodology ensures coherent linkage of climate smart agriculture (CSA) process with stakeholders needs and challenges;\r\n\r\nThis approach is successful to identify CSA implementation feasibility, barriers and incentive mechanisms that play a major role in guiding investment decisions by governments, private sector and farming communities on CSA\r\n', 2018, 'https://www.dropbox.com/s/qc3z0tem1avgcij/Stakeholders%20Prioritization%20of%20Climate-Smart%20Agriculture%20Interventions_Evaluation%20of%20a%20Framework.pdf?dl=0', 1, 'India', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=330&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 331, 'Protocol for characterizing community-based rangeland management cases', 'This is research protocol for comparative case study analysis of instances of institutional support for communities establishing community-based rangeland management.  It adopts an Option by Context approach, guiding the structured collection of variables on (a) the \"option\" (strategy and approach used by a change agent in terms of governance model/institutional structure), (b) the social and biophysical context, and (c) the outcomes.', 2018, 'https://cgspace.cgiar.org/handle/10568/97916', 1, NULL, 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=331&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 335, 'Picture-based insurance: delivering affordable crop insurance using farmers\' smartphone pictures to assess crop damage', 'Farmers register each site to be insured through an initial picture taken via a smartphone App. They take follow-up pictures to trigger personalized advisories, monitor crop health, and document losses. When assessing claims, experts evaluate the damage using the pictures. The pictures are geotagged and time-stamped, and the App has built-in features to prevent tampering with pictures. As more training data become available, machine learning is used to build automated image-processing algorithms.', 2018, 'http://ebrary.ifpri.org/cdm/singleitem/collection/p15738coll2/id/131380/rec/15', 1, 'Ethiopia,India,Kenya', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=335&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 336, 'Method to help countries define their trade liberalization strategies', 'Implementation of the African Continental Free Trade Agreement leads to challenges for African countries. Although most products have to be included in the trade liberalization scheme, each country may select âsensitive productsâ which will be exempt from this scheme. To help countries make this strategic decision, PIM has developed a method now used by various organizations including the United Nations Economic Commission for Africa to provide assistance to individual countries in the trade liberalization process.', 2018, 'https://www.uneca.org/sites/default/files/PublicationFiles/afcfta-towards_the_finalization_of_modalities_on_goods_rev1.pdf', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=336&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 337, 'Findings on the effects of combining lump sum cash transfers with technical and farm management advice to increase productivity and link farmers to value chains', 'A pilot project modeled after a Brazilian government-run program (Fomento) was implemented and evaluated with partners in Malawi and Senegal. The program combined extension advice and a lump sum payment, to better enable farmers to make investments which would allow them to participate in value chains (groundnut in Senegal and soybean in Malawi). In both countries, impacts on productivity measures were observed by the end of the project; the studies are being finalized.', 2018, 'http://ebrary.ifpri.org/utils/getfile/collection/p15738coll2/id/131352/filename/131563.pdf; https://ipcig.org/pub/eng/OP325_Adapting_Fomento_to_countries_in_Sub_Saharan_Africa.pdf', 1, 'Malawi,Senegal', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=337&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 338, 'The CCAFS Regional Agricultural Forecasting Tool (CRAFT) will help bring powerful advance information tools to farmers and agricultural decision makers, better allowing them to manage within-season climate risk to agriculture', 'CRAFT is a publicly accessible Windows desktop application that supports multiple crop models and can produce multiple simulation scenarios, maps, and interactive visualizations for regional crop yield forecasting, climate change and risk assessment. CRAFT can assess future climate risk for crop production and determines high climate-risk areas for crop yield. It can help a diverse range of stakeholders by providing reliable information on in-season yield forecasting and spatial and temporal variability of crop production.\r\n\r\n', 2018, 'CRAFT is available at: https://dssat.net/2919; see also https://doi.org/10.1016/j.envsoft.2019.02.006', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=338&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 339, 'Gender-sensitive LINK methodology for making business models more gender inclusive', 'The LINK methodology provides a process and tools for making business models more inclusive. The gender-sensitive LINK methodology provides tools for assessing and improving inclusion from a gender perspective.', 2018, '', 1, 'Honduras', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=339&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 340, 'Measurement, Reporting and Verification (MRV) Platform for Agriculture', 'The MRV Platform for Agriculture contains tools, approaches, and case studies for MRV of GHG emissions and mitigation actions in the agriculture sector. The platform initially focuses on MRV resources specific to livestock, but will accommodate MRV for other agricultural sectors and cross-cutting issues over time.\r\n\r\nThis platform provide useful information to guide the technical and institutional design of MRV systems for agricultural mitigation actions, including those outlined in NDCs, NAMAs, and AFOLU projects.', 2018, 'https://www.agmrv.org', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=340&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 342, 'Innovative use of sheep and goats by women in climate smart villages in Kenya', 'The CGIAR led project thus introduced improved strains of resilient but more productive indigenous breeds of Gala goats and Red Maasai sheep for crossbreeding with the local breeds and has resulted in women taking a lead in community led breeding programmes resulting in crosses with better performance and desirable traits.', 2018, 'https://ccafs.cgiar.org/publications/innovative-use-sheep-and-goats-women-climate-smart-villages-kenya#.XPpHLy2B2i4', 1, 'Kenya', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=342&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 344, '\"Agroclimatic similarity\" variable developed to improve the measurement of the spatial spillover potential of agricultural R&D investments', 'A novel variable, the agroclimatic similarity between the locations of plant breeding institutes and the locations of the farms that adopt the improved technologies developed in these institutes, is introduced as a key factor affecting the spatial variations of agricultural productivity potential and agricultural transformation patterns. This approach provides a useful indicator of spatial variations in spillover potentials of agricultural technologies developed through public investments. \r\n', 2018, 'https://www.researchgate.net/publication/328124292_Geography_of_plant_breeding_systems_agroclimatic_similarity_and_agricultural_productivity_evidence_from_Nigeria', 1, 'Nepal,Nigeria', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=344&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 345, 'Rural Investment and Policy Analysis model ', 'The Rural Investment and Policy Analysis model (RIAPA) is an economywide model that evaluates alternative policy and investment options based on their impacts on economic growth, job creation, poverty reduction, and dietary change. RIAPA captures farm and non farm components of the agri-food system, and links producers and consumers in rural and urban areas. ', 2018, 'https://onlinelibrary.wiley.com/doi/full/10.1111/agec.12412', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=345&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 349, 'Value chain nominal rate of protection methodology', 'The nominal rate of protection methodology was extended to a value chain framework. The proposed indicator, value chain nominal rate of protection, allows policy makers to see an aggregate measure of policy impacts on all the commodities and products in the value chain, normalized at the farm level. The methodology was applied to selected value chains in India. ', 2018, 'https://doi.org/10.1111/1477-9552.12305', 1, 'India', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=349&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 351, 'Intercropping with green manure cover crops (GMCC)', 'We tried Intercropping with green manure cover crops-GMCC inter cropping strategies with 7 different legume combinations in maize-based systems. These have been successful in Murehwa District', 2018, 'https://www.reuters.com/article/us-zimbabwe-farming-drought/faced-with-more-drought-zimbabwes-farmers-hang-up-their-plows-idUSKBN19002D', 1, 'Zimbabwe', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=351&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 352, 'Methodology for identifying diversification options for climate change adaptation', 'TBA', 2018, 'https://www.bioversityinternational.org/e-library/publications/detail/diversification-for-climate-change-resilience-participatory-assessment-of-opportunities-for-diversi/', 1, 'Guatemala,India,Mali', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=352&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 354, 'Agrobiodiversity Index ', 'The Agrobiodiversity Index is the tool we need to measure agrobiodiversity and identify concrete actions to achieve diverse and sustainable food systems. It will contribute to sustainability through three pillars: diets and markets, production systems and genetic resources. The Agrobiodiversity Index will help decision-makers â governments, investors and companies â ensure that food systems are more diverse and sustainable', 2018, 'https://www.bioversityinternational.org/abd-index/', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=354&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 357, 'Incorporating Genome-wide Association Mapping Results into Genomic Prediction Models for Grain Yield and Yield Stability in CIMMYT Spring Bread Wheat', 'The first time in wheat that GWAS results were incorporated into GS prediction models', 2018, 'https://www.globalrust.org/content/marker-and-haplotype-based-association-mapping-and-genomic-prediction-unravel-complex; https://repository.cimmyt.org/bitstream/handle/10883/19879/59975.pdf?sequence=1&isAllowed=y', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=357&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 358, 'Application freely available to farmers which provides crop recommendation', 'Project using satellite data to help Mexican smallholder farmers grow sugar cane and wheat with optimized crop productivity by identifying factors that cause the yield gap between crop potential and actual field performance. Mobile application aimed at helping Mexican farmers improve crop productivity and stabilize their incomes', 2018, 'https://www.rezatec.com/mobile-app-helping-mexican-farmers-improve-sugar-cane-and-wheat-yield-through-the-analysis-of-satellite-data/', 1, 'Mexico', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=358&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 360, 'Pronosticos AClimateColombia: A system for the sustainable provision of agro-climatic information for agricultural adaptation in Colombia', 'A digital agro-climatic forecast system for the provision of climate services in Colombia. The system, named âPronosticos AClimateColombiaâ, formalizes the processing of climate and crop information from quality control, forecasting, and tailoring to crop-specific decision making processes. The design, development and evaluation process captured user needs by involving key stakeholders ranging from the Ministry of Agriculture, the National Meteorological Service (IDEAM), as well as farmer organizations and farmers in a range of agricultural areas.', 2018, '', 1, 'Colombia', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=360&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 362, 'Methodology for profiling indigenous food systems (elements, sustainability and resilience)', 'A participatory approach to characterize and assess resilience of Indigenous Food Systems to strengthen local capacities and inform global debates on sustainability. The main aim of the method is to document the inputs and outputs of the food system, links to the market, and indigenous practices and knowledge that support resilience and sustainability. ', 2018, 'https://drive.google.com/open?id=1yl5_zhwekkYFinKNptg1qySFTV6tuGbQ', 1, 'Cameroon,Colombia,Guatemala,India,Kyrgyzstan,Mali,Peru,Solomon Islands,Vietnam', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=362&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 363, 'E-learning course on Mainstreaming Biodiversity for Food and Nutrition (available in English, Portuguese and Turkish)', 'This online course is divided into 4 modules, with 7 interactive lessons, covering:\r\nThe main issues and barriers of the current food production and consumption patterns and how they affect biodiversity;\r\nThe importance of biodiversity and how it can be integrated across different sectors to create more sustainable and nutritious food systems;\r\nInternational and national (Brazilian case study) policies and initiatives related to biodiversity conservation, food and nutrition security;\r\nPractical tools, metrics and indicators.', 2018, 'http://www.b4fn.org/e-learning/', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=363&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 364, 'Biodiversity Mainstreaming for Healthy and Sustainable Food Systems - A toolkit', 'This is an open-access guide to mainstreaming biodiversity that draws on case studies across the four partner countries, Brazil, Kenya, Sri Lanka and Turkey, outlining steps to provide evidence, influence policies, and raise awareness.', 2018, 'http://www.b4fn.org/the-mainstreaming-biodiversity-toolkit/', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=364&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 365, 'Using data from the Living Standards Measurement Study (LSMS), (non-food consumption data) to assess dietary intake and monitor dietary changes', 'Using LSMS (non-food consumption data) to assess dietary intake and monitoring dietary changes', 2018, 'https://anh-academy.org/sites/default/files/Combined%20PPTs_Measuring%20the%20healthiness%20of%20diets_ANH2018%20in%20Accra.pdf', 1, 'Bangladesh,Ethiopia,Nigeria,Vietnam', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=365&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 366, 'Food system assessment in local settings (transects, food flow in Ethiopia, etc)', 'CIAT and WUR contribution to this Innovation (Stef de Haan (transect); Inge Brouwer (Food flow); Marrit vd Berg (food environment)\"\r\n', 2018, 'Food flow: no open access. Zarifopolou A, Peletier J. Follow the food: Mapping food system dynamics in a rural-urban gradient in Southern Ethiopia. MSc Report, Wageningen University, Wageningen, 2018', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=366&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 367, 'Nutrition incentive in dairy contract farming in Northern Senegal', 'A nutrition incentive (micro-nutrient fortified yogurt) was introduced in the contract between semi-nomadic milk suppliers and a dairy processing factory in Northern Senegal in order to improve the regularity of milk delivery while also improving child nutrition. The study showed that the introduction of this incentive led to significant increases in the frequency of milk delivery but had limited impacts on total volume of milk delivered. ', 2018, 'https://academic.oup.com/ajae/article-abstract/101/2/404/5046068?redirectedFrom=fulltext', 1, 'Senegal', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=367&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 368, 'Methodology for dietary gap analysis at national level ', 'Methodology to analyse dietary gaps at national level using non-food consumption data, using four different indicators: household dietary diversity score, Dietary Approaches to Stop Hypertension (DASH) diet score, Global Burden of Disease score and nutrient gap. ', 2018, 'https://anh-academy.org/sites/default/files/Combined%20PPTs_Measuring%20the%20healthiness%20of%20diets_ANH2018%20in%20Accra.pdf', 1, 'Bangladesh,Ethiopia,Nigeria,Vietnam', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=368&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 370, 'Methodology to analyse national food systems based on secondary reports and data: applied in Ethiopia ', 'Using a participatory process, stakeholders and researchers identify research streams to support the operationalizing of food systems\r\napproaches in Ethiopia. The published product provides a summary review of the literature, applies a food systems framework to research priorities in the Ethiopian context, develops an initial food systems profile of Ethiopia, and identifies 25 priority research questions, categorized into three main areas. ', 2018, 'http://ebrary.ifpri.org/utils/getfile/collection/p15738coll2/id/132417/filename/132627.pdf; https://marlo.cgiar.org/projects/A4NH/study.do?expectedID=2218&edit=true&phaseID=59', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=370&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 374, 'Development of the process and methodology to assess drivers of vegetables intake', 'This study aimed to provide insights into vegetable consumption behavior of urban Nigerian consumers across different Socio-Economic Classes (SEC), their main food choice motives, and the associations of these motives and other drivers with vegetable consumption.', 2018, 'https://doi.org/10.3390/su10124771', 1, 'Nigeria', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=374&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 375, 'Methodology to analysis highly informal dairy markets and its consumers', 'With the need for high quality, unbiased, consumer market data and the latest on-the-ground information, Euromonitor collaboration GAIN embarked on  methodology developmentto collect robust, standardised market data to be used to monitor health intervention strategies, improve consumer health and support SMEs in the supply chain. The methodology is applied to the dairy sector in Ethiopia, to identify dairy products typically consumed and point of purchased, size the dairy market and understanding of the brands available.', 2018, 'no evidence yet, methodology is under embargo untill fully tested', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=375&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 376, 'Protocol to assess and reduce post-harvest losses in tomato value chain', 'The framework highlights the interconnected nature of problems the supply chain and the need to look at the whole system instead of specific stages, when proposing solutions. Practices and underlying causes contributing to food loss are identified for each stage of the supply chain using a literature search. Deductive logic is used to fill the gaps where literature was found to be scarce, and to derive socioeconomic indicators that signal the presence of identified causes.', 2018, 'https://doi.org/10.3390/su11030579', 1, 'Nigeria', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=376&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 377, 'Tool (crates) to reduce post-harvest losses for tomatoes ', 'Testing out methodology to reduce food waste by the use of plastic crates in stead of woven baskets in the tomato market in Nigeria', 2018, 'https://doi.org/10.3390/su11010247', 1, 'Nigeria', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=377&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 378, 'Veggies on Wheels intervention for urban consumers in Nigeria', 'The veg-on-wheels intervention offered fresh and ready-to-cook vegetables to urban Nigerian consumers. The ready-to-cook green leafy vegetables were sold in cool boxes on bikes and push carts. This way the vegetables could stay fresh and could be sold at convenient locations close to work places.\r\n\r\n', 2018, 'https://www.mdpi.com/2071-1050/10/12/4771 ', 1, 'Nigeria', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=378&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 379, 'Methdology for stakeholder-led identification of platforms for healthier diets and the role of platforms as mechanisms to support the scaling and anchoring of food system transformations for healthier diets', 'Inclusive and sustainable food systems should guarantee consumers availability of, equitable access to and efficient delivery of safe and healthy food. This requires active networking between multiple stakeholders, engaged in different parts of the food system. This methodology uses network analysis to visualise relevant platforms, clustering organisations and identifying key connectors (multiple memberships).', 2018, 'http://edepot.wur.nl/472117', 1, 'Bangladesh,Ethiopia,Nigeria,Vietnam', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=379&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 381, 'Women\'s Empowerment in Agriculture Index for Value chains (WEAI4VC)', 'The first pilot assessed empowerment of men and women as different types of value chain actors (producers, entrepreneurs, and wage workers) in Bangladesh. A second pilot in the Philippines looked at empowerment of men and women working in production, processing, trading, and marketing in abaca, coconut, seaweed, and swine value chains. The third pilot in Honduras adapted the WEAI4VC questionnaires from the Philippines study to the context of cashew, fruits, and dairy value chains. ', 2018, 'http://ebrary.ifpri.org/cdm/ref/collection/p15738coll2/id/132874', 1, 'Bangladesh,Honduras,Philippines', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=381&phaseID=57');
