INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 419, 'Information and Communications Technology (ICT)-based AppââYeZaReââfor disseminating climate and market information to smallholder farmers developed and is being tested in northern and southern Ethiopian highlands through a public-private partnership', 'An ICT-based Appâfor providing weather forecasts, agro-advisory and market information to smallholder farmers developed and is being tested with smallholder Ethiopian farmers. Through a mobile App and SMS, location-specific weather forecasts bundled with market prices is disseminated to inform farmer decision-making. Weather information includes 3, 10, 30-days and seasonal forecast on rainfall onset and cessation. The 10-days forecast includes advisory service with specified management actions. Tailored agro-meteorological advisory allows informed decision-making that maximize agricultural productivity.\r\n', 2018, 'http://yezare.info/', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=419&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 422, 'Adoption and testing of the Gold Standard Smallholder Dairy Methodology', 'In cooperation with UN FAO and ILRI, a methodology for quantification of GHG emission reductions from smallholder dairy productivity improvements has been developed. The methodology was formally approved by the Gold Standard in November 2016. \r\nIn 2018, the methodology was tested by the implementation of a baseline survey of 429 households in central Kenya. The survey collected data required for GHG estimation, data on adoption of management practices, sources of finance and gender roles.  ', 2018, 'https://www.goldstandard.org/sites/default/files/documents/gs_dairy_methodology.pdf', 1, 'Kenya', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=422&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 424, 'Behavior change communication campaign on awareness and prevention of zoonoses, designed for health workers to deliver to patients', 'Public health campaign/materials designed for health care workers and their patients to raise awareness of zoonotic diseases and how to prevent them ', 2018, 'https://cgspace.cgiar.org/handle/10568/80714', 1, 'Kenya', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=424&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 426, 'Video clips on Rift Valley fever customised for dissemination using smart phones  ', 'https://ilvac.net/2018/06/22/introducing-english-and-swahili-instructional-videos-on-the-patterns-signs-symptoms-and-control-of-rift-valley-fever/', 2018, 'https://ilvac.net/2018/06/22/introducing-english-and-swahili-instructional-videos-on-the-patterns-signs-symptoms-and-control-of-rift-valley-fever/', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=426&phaseID=59');
