INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 428, 'Qualitative methodological approach to better understand the socioeconomic factors that influence adoption of Climate-Smart Agricultural options in smallholder farming communities. ', 'Development and piloting of a qualitative methodological three-step methodology aiming to answer: (1) Which CSA options promoted by CCAFS have been adopted by farmers? (2) Who within the community is adopting which CSA option(s)? What is the diversity in the types of farmers according to their CSA adoption profile? (3) What are the motivations and enabling/constraining factors for each farmer type to adopt a CSA option?\r\n ', 2018, 'https://cgspace.cgiar.org/handle/10568/98602; https://ccafs.cgiar.org/research-highlight/what-are-socioeconomic-factors-influence-csa-adoption#.XJFG8yJKiUk', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=428&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 429, 'Climate Smart Agriculture (CSA) monitoring framework to track adoption, outcomes, synergies and tradeoffs at household and farm level', 'The proposed integrated CSA framework is made of a set of 44 standard indicators (across the three CSA pillars) and a user friendly ICT-based data collection App, allowing to systematically i) assess adoption trends, motivations and constraining factors (community level), and ii) generate new evidence-based knowledge on location-specific perceived effects of CSA options at farm and household levels.\r\n\r\n', 2018, 'https://cgspace.cgiar.org/handle/10568/100318; https://cgspace.cgiar.org/handle/10568/100253; https://cgspace.cgiar.org/handle/10568/100235; http://rpubs.com/g-bejarano/DatosGenerales', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=429&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 430, 'Updated social accounting matrix for the Sudan', 'The 2012 social accounting matrix (SAM) for the Sudan has a special focus on agriculture, water, and energy.', 2018, 'http://ebrary.ifpri.org/cdm/ref/collection/p15738coll3/id/328', 1, 'Sudan', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=430&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 431, '(Arab) Agricultural Investment for Development Analyzer ', 'The (Arab) Agricultural Investment for Development Analyzer (AIDA) is an agricultural and rural economic planning toolkit designed to help governments and analysts draw agricultural investment plans and ensure that levels of investment in the agricultural and rural sectors is sufficient for achieving the desired outcomes. AIDA has been built using an economywide and minimalistic investment data approach.', 2018, 'http://ebrary.ifpri.org/cdm/ref/collection/p15738coll2/id/132267', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=431&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 433, 'Tools for the USAID Global Food Security Strategy transition framework', 'The USAID Global Food Security Strategy (GFSS) transition framework is used to monitor country progress towards an end state where they can transition out of GFSS target country status.  PIM developed 1) a tool which aggregates into three composite dimensions (level of need, commitment to food security, and country capacity) the data on the indicators used to monitor country progress, and 2) a scorecard approach to visually represent country progress. ', 2018, '', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=433&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 435, 'New economic growth model that includes the informal sector', 'The Lewis dual-sector model was adapted to include the \"in-between\" sector (micro, small and medium enterprises in the informal economy). The model provide a partial explanation for recent patterns of growth in Rwanda where the in-between sector has expanded more rapidly than the tradable sector.', 2018, 'https://www.sciencedirect.com/science/article/pii/S0305750X16305721?via%3Dihub', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=435&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 439, 'Investment pathways (Stepwise approach) tailored to specific farmer segments for improving resilience and smart agriculture practices', 'To support CSA recommendations, IITA developed investment pathways known as the Stepwise approach - which looked in more detail at which good agricultural practices need to be put into place for which typology of farmer to reach different degrees of productivity and profitability. We then went on to overlay the identified CSA recommendations with the IITA Stepwise investment pathway which resulted in the final list of CSA recommendations that have been clustered in \'packages\'.', 2018, 'https://hdl.handle.net/10568/100136 ', 1, 'Uganda', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=439&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 440, 'Developing, testing and making available an integrated climate and agro-climate advisory to enhance adaptive capacity and sustainable agricultural productivity in Ethiopia', 'This on-line tool aims to increase agricultural production and resilience to climate variability and change through effective integrated AgroClimate Advisory system to advise smallholder farmers, private sector, policy-makers, federal and state government, research and higher learning institutions in Ethiopia. The system architecture include systems layers and components that include forecast generators and apps, climate model that configures parameters of climate prediction for different agro-ecological zones and integrates contextualized crop models for key/major food crops. ', 2018, 'https://ethioagroclimate.org/', 1, NULL, 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=440&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 441, 'Maize genotypes resistant to the devastating Corn Stunt Complex disease ', 'We developed 100 inbred lines that carry resistance to  of Corn Stunt Complex (CSC) disease. CSC is a very important disease of maize in many Latin American Countries and can cause close to 100% yield losses in susceptible maize plants during severe epidemics.       ', 2018, 'https://repository.cimmyt.org/xmlui/bitstream/handle/10883/17843/58049.pdf?sequence=1&isAllowed=y', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=441&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 442, 'Biological control product for controlling aflatoxin contamination in maize grain', 'We are in the process of development of biological control methods of controlling aflatoxin contamination in maize grain. These are certain bacterial strains that reduce the development of of some fungal species that produce substances (secondary metabolites) that are very toxic to humans and animals. Aflatoxin contamination of food is especially important in developing countries. Our preliminary results are very promising and we need to repeat the experiment one more year to prove the concept.  ', 2018, 'https://drive.google.com/file/d/1KH_GjTAnKYY4nCPGSpbts7sid09LVbyE/view?usp=sharing', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=442&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 444, 'Updated Tropical Forages Tool (formerly known as SoFT)', 'The Tropical Forages tool is currently being updated in terms of content and technical features. The content was updated to the status quo of actual research and new technical features include e.g. a mobile app and automatic translation through google translator.', 2018, 'https://cgspace.cgiar.org/handle/10568/99215', 1, NULL, 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=444&phaseID=60');
