INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 456, 'New Megathyrsus hybrids', 'Most Megathyrsus maximus accessions are characterized by high biomass and excellent nutritional quality, but they fail in the adaptation to limiting conditions, which makes them only suitable for high input agriculture. We are investing all our efforts to evolve new hybrids more rustic that can easily adapt to the conditions of low input agriculture, while maintaining their performance in terms of Biomass production and nutritional quality.', 2018, 'https://cgspace.cgiar.org/handle/10568/99363', 1, NULL, 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=456&phaseID=60');
