INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 461, 'International Model for Policy Analysis of Agricultural Commodities and Trade (IMPACT) webtool ', 'Tool hosted on the IFPRI Food Security portal presenting various results from the IMPACT model and based on different studies and reports for the Global Food Security Report.', 2018, 'https://csi.maps.arcgis.com/apps/Cascade/index.html?appid=66ba20e26de84d6797fd1ec3a6703d7c', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=461&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 463, 'Improvement of wheat landraces, collected from farmers, through evaluation and selection.', 'The landraces were collected form the farmers. Since mots of them are mixtures - they went through analytical selection in 3-4 years and evaluation to identify the best components of the landrace and re-create the population to be provided back to farmer. ', 2018, 'https://www.wheatlandraces.org/', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=463&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 464, 'Development and utilization of primary winter synthetics to incorporate important new traits in winter wheat varieties. ', 'New synthetic winter wheat varieties were developed using winter durums from Ukraine and Romania and Ae. taushii. The new synthetic wheat contained important sources of resistance to diseases such as rusts. They also demonstrated strong grain mineral content and drought tolerance. The innovation is the techniques used. ', 2018, 'http://www.iwwip.org/Nursery', 1, 'Kazakhstan,Russian Federation,Turkey,Uzbekistan', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=464&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 465, 'Elite winter wheat lines.', 'New winter and facultative wheat varieties developed by IWWIP (Turkey-CIMMYT-ICARDA) for irrigated and semi-arid environments and it has been made available to public and private breeding and research programs globally.', 2018, 'http://www.iwwip.org/Nursery', 1, 'Georgia,Kazakhstan,Russian Federation,Turkey,Uzbekistan', 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=465&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 470, 'Village Seed Banks have been developed through this project, playing a vital role in varietal turnover. ', 'The project has supported the development of local seed system, through farmer owned seed business. These businesses are critical for ensuring old varieties are replaced with the improved varieties containing the traits necessary for the areas in question. The banks sell quality wheat seed for up to 12 percent less than local markets', 2018, 'https://wheat.org/2018-agricultural-innovation-program-meeting-cimmyt-and-partners-achievements-in-pakistan/; https://aip.cimmyt.org/wp-content/uploads/sites/30/2019/01/6-AIP-Semiannual-Technical-report-April-2018-September-2018.pdf', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=470&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 473, 'Options for keeping the food system within environmental limits', 'We show that between 2010 and 2050, as a result of expected changes in population and income levels, the environmental effects of the food system could increase by 50â90% in the absence of technological changes and dedicated mitigation measures, reaching levels that are beyond safe planetary boundaries. We find that a synergistic combination of measures (dietary changes, improvements in technologies/management, reductions in food losses) are needed to sufficiently mitigate the projected increase in environmental pressures.', 2018, 'https://www.nature.com/articles/s41586-018-0594-0', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=473&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 475, 'New maize breeding objectives based on a comprehensive understanding of usersâ preferences for maize products in Africa', 'This research studied the most important maize-based foods in Africa and their respective traditional processing techniques, assessed maize preferences, analysed physical and chemical characteristics of maize, estimated the contribution of maize to the daily diet of Africans, assessed the impact of traditional processing on nutritional composition of maize, and, lastly, formulated crucial breeding objectives to meet usersâ preferences.', 2018, 'https://www.sciencedirect.com/science/article/pii/S221191241730161X', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=475&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 476, 'New rice dual purpose cultivars', 'Cultivars that are superior in food and fodder traits', 2018, 'https://cgspace.cgiar.org/handle/10568/99322', 1, 'Philippines', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=476&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 478, 'Determinants of maize cultivation in a land-scarce rice-based economy in Bangladesh', 'Maize (Zea mays L.) use, mostly as poultry and fish feed, has been increasing rapidly in Bangladesh â a densely populated country with a rice-based agri-food system. Domestic maize production has increased despite land scarcity but failed to meet the growing demand, with imports making up the shortfall. The present study contextualizes Bangladeshâs domestic maize production potential and examines the determinants of current maize cultivation using data from 1.12 million farm households.', 2018, 'https://www.tandfonline.com/doi/abs/10.1080/15427528.2018.1446375', 1, 'Bangladesh', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=478&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 483, 'Poverty sensitive scorecard tool: combining risk scoring with poverty scoring to help lenders and policy makers prioritize development projects', 'This scorecard tool contains two innovations. 1) The risk-scoring algorithm is built on the latest developments in statistical modeling, which improves the accuracy of the risk assessments. 2) By combining risk scoring and poverty scoring, the instrument goes beyond standard poverty-targeting approaches to prioritize lending or grant allocation: it ranks projects that target the poor AND are sustainable. ', 2018, 'https://www.sciencedirect.com/science/article/abs/pii/S0306919217301409?via%3Dihub', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=483&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 484, 'Analytical approach for predicting potential areas of agroforestry expansion', 'CCAFS/ICRAF developed an analytical approach based on expansion domains. These domains establish the suitable areas based on both biophysical and socioeconomic characteristics for agroforestry species and systems. This provides a realistic picture of where it will be possible to use agroforestry and where it will not. The analytical framework also estimates the costs of implementation for each agroforestry system based on secondary data that ICRAF compiled in its Viet Nam Agroforestry Database.', 2018, 'https://cgspace.cgiar.org/handle/10568/98867', 1, 'Vietnam', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=484&phaseID=56');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 485, ' New Varieties adopted: Mayi Plus 1 ', 'CIMMYT maize yield trials have identified two varieties that out-yield anything grown in Haiti in both irrigated and rain-fed conditions.  Basically, CIMMYT has identified the best varieties for the next decade in Haiti. ', 2018, 'Link not avialable', 2, 'Haiti', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=485&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 486, 'Understanding the effects of emergency school feeding and general food distribution on children\'s schooling in conflict areas', 'Using data from a unique pre-crisis baseline and five-year follow-up, the study investigated effects of emergency school feeding and general food distribution on childrenâs schooling during conflict in Mali. Both methods of food assistance had different effects on outcomes, emphasizing the need for joint programming between humanitarian responses and education in conflict areas. The educational implications of food assistance should be considered in planning humanitarian responses to bridge the gap between emergency assistance and development.', 2018, 'https://www.unicef-irc.org/publications/956-school-feeding-or-general-food-distribution-quasi-experimental-evidence-on-the-educational.html', 1, 'Mali', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=486&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 487, 'Understanding the impacts of the World Food Programme\'s food assistance on food consumption and child nutrition in conflict-affected areas', 'The effects of food assistance on childrenâs height and caloric and micro-nutrient consumption were concentrated in areas not in the immediate vicinity of the conflict, while an increase in household food expenditures was observed closed to the location of armed groups. The effects of food assistance were higher for households receiving at least two forms of food assistance.', 2018, 'https://www.sciencedirect.com/science/article/pii/S0305750X18300378?via%3Dihub', 1, 'Mali', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=487&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 488, 'Nutrition-sensitive social protection interventions increase the use of multiple-micronutrient powders and iron supplements in rural preschool Bangladeshi children', 'Cash/food transfers accompanied by nutrition counseling increased the likelihood that children aged 6-59 months had ever consumed multiple-micronutrient powders, had consumed multiple-micronutrient powders in the preceding week, and had consumed either multiple-micronutrient powders or an iron supplement in the preceding week. The combination of transfers and nutrition counseling also increased maternal knowledge of iron deficiency and awareness of multiple-micronutrient powders. These Impacts differed from those of transfers alone, highlighting the importance of complementary nutrition counseling.\r\n', 2018, 'https://www.cambridge.org/core/journals/public-health-nutrition/article/randomized-control-trials-demonstrate-that-nutritionsensitive-social-protection-interventions-increase-the-use-of-multiplemicronutrient-powders-and-iron-supplements-in-rural-preschool-bangladeshi-children/1E8022AF54A6C6EB21801B61FCD9C54C', 1, 'Bangladesh', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=488&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 492, 'Scaling conservation agriculture-based sustainable intensification systems in Ethiopia', 'Several activities have been conducted on identifying, synthesizing and mapping sustainable maize production systems in Africa since 2012.  Research evidences on sustainable production systems (improved variety targeting combined with conservation agriculture) were synthesized and a policy engagement works done over two years resulted in adoption of conservation agriculture -based sustainable intensification by the Ministry of Agriculture of Ethiopia.', 2018, '', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=492&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 493, 'Use of high-throughput color sorting technology to enrich source breeding populations for deep orange color required for maize varieties containing high pro-vitamin A content. ', 'In breeding populations targeting increased pro-vitamin A, the product profile includes a recognizable deep orange color.  We plan to use high-throughput optical sorting technology to enable us to rapidly screen very large populations of seeds to sub-set the most orange kernels for further breeding work.', 2018, 'N/A', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=493&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 494, 'Seed Production Technology ', 'Seed Production Technology (SPT) system prevents specific plants from producing pollen.  The system enables production of non-pollen producing early generation seed (EGS) which is non GM.  SPT produced EGS results in cost savings to seed companies, improves the hybrid purity of commercial seed, and results in a hybrid product which has 50% non pollen producing plants and 4 to 5% increased grain yield under farmer conditions.', 2018, 'N/A', 1, NULL, 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=494&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 495, 'Ex-ante analysis to determine potential benefits from BNI-technology', 'Incorporating Biological Nitrification Inhibition into crops offers the potential of simultaneously increasing Nitrogen use efficiency and reducing nitrous-oxide emissions into the atmosphere and thus reducing the greenhouse gas emission  footprint of agricultural production systems.\r\nBNI will potentially be available in crop varieties for farmers in 12 to 25 years from now. Time-frame depends on crop, the environment, management options and the broad socio-economic context, requiring both foresight and ex-ante impact assessment.\r\n', 2018, 'https://repository.cimmyt.org/bitstream/handle/10883/19817/59914.pdf?sequence=1&isAllowed=y', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=495&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 496, 'ex-ante impact assessment of wheat blast in south Asia', 'A spatial mapping and ex ante study regarding the risk and potential spread in South Asia of wheat blast, a mysterious and deadly disease from the Americas that unexpectedly infected wheat in southwestern Bangladesh in 2016, identified 7 million hectares of wheat cropping areas in Bangladesh, India, and Pakistan whose agro-climatic conditions resemble those of the Bangladesh outbreak zone.', 2018, 'https://wheat.org/call-for-action-on-wheat-blast-threat-in-south-asia/', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=496&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-17', 'Wheat', 'AR', 2018, 'Yes', 497, 'Fluctuating milling quality in wheat: The Achilles Heel of Cereal Foresight Studies?', 'One of the expected consequences of climate change is the occurrence of more erratic rainfall patterns. This implies that there will always be production areas that will be confronted with weather conditions that are not conducive to high levels of wheat grain quality (milling quality, processing and end-use quality). The question is, if fluctuating grain quality, matters in the long-run for food security and, if fluctuating wheat grain quality could spark a food crisis. ', 2018, 'https://repository.cimmyt.org/handle/10883/19885', 1, NULL, 'https://marlo.cgiar.org/summaries/Wheat/projectInnovationSummary.do?innovationID=497&phaseID=62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 502, 'Methodology for using GIS in chicken breeds suitability map in Ethiopia', 'Methodology for assessing the productivity of improved chicken genotypes in different agro-ecologies (on-station) ', 2018, 'https://cgspace.cgiar.org/handle/10568/98850', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=502&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 503, 'A panel of single nucleotide polymorphisms (SNPs) for genomic imputation and admixture proportion analysis in East African dairy crossbreed breeds', 'Cost-effective high-density (HD) genotypes of livestock species can be obtained by genotyping a proportion of the population using a HD panel and the remainder using a cheaper low-density panel, and then imputing the missing genotypes that are not directly assayed in the low-density panel. ', 2018, 'https://cgspace.cgiar.org/handle/10568/96904', 1, NULL, 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=503&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-22', 'CCAFS', 'AR', 2018, 'Yes', 507, 'Feeding cassava leaves to livestock for reducing methane emissions', 'Through an in-vitro experiment we demonstrated the potential of cattle diets with cassava supplements to reduce methane emissions.  The practice of feeding cassava leaves to livestock is common during the dry season (when feed availability is low) among livestock farmers in northern Colombia. Our work demonstrated that supplementing livestock feed with cassava leaves may contribute towards reductions in enteric methane emissions.  ', 2018, 'https://www.mdpi.com/2071-1050/9/11/1891', 1, 'Colombia', 'https://marlo.cgiar.org/summaries/CCAFS/projectInnovationSummary.do?innovationID=507&phaseID=56');
