INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-24', 'WLE', 'AR', 2018, 'Yes', 585, 'WABEF, a toolkit to promote anaerobic digestion of bio-wastes in West Africa', 'WABEF is a compendium of data sheets and databases (3) to estimate the wastes from agricultural activities, municipal activities, and ago-industrial activities. It suggests anaerobic digestion technologies to use up these bio-wastes. It uses a simulation model (Approzut and UPUTUC simulation) to compare the mass balances and assess the investment and operating costs of these technologies to support decision making by governments, communities, industries and farmers. ', 2018, 'https://wabef.cirad.fr/content/download/4676/34741/version/1/file/WABEF+final+narrative+report_submitted.pdf', 1, NULL, 'https://marlo.cgiar.org/summaries/WLE/projectInnovationSummary.do?innovationID=585&phaseID=58');
