INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 600, 'Alternate wetting and drying (AWD) in Africa', 'Alternate wetting and drying is a water saving technology that enables irrigated rice cultivation with less water than the conventional practice of maintaining the field continuously flooded. Under AWD, ï¬elds are subjected to intermittent ï¬ooding where irrigation is interrupted, and water is allowed to subside until the soil reaches a certain moisture level, after which the field is re-flooded. It has been reported that this technology can reduce water input and GHG without yield penalty.', 2018, 'https://doi.org/10.3390/w10060711 ', 1, 'Burkina Faso,CÃ´te d,Madagascar,Senegal', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=600&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 602, 'Alternate wetting and drying (AWD) in Asia', 'Alternate wetting and drying is a water saving technology that enables irrigated rice cultivation with less water than the conventional practice of maintaining the field continuously flooded. Under AWD, ï¬elds are subjected to intermittent ï¬ooding where irrigation is interrupted, and water is allowed to subside until the soil reaches a certain moisture level, after which the field is re-flooded. It has been reported that this technology can reduce water input and GHG without yield penalty.  ', 2018, '', 1, 'Bangladesh,China,Philippines,Thailand,Vietnam', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=602&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 604, 'Weather-rice-nutrient integrated decision support system (WeRise)', 'WeRise is a seasonal climate prediction-based decision support system for rainfed rice farmers through integration of statistical downscaling model of GCM and crop growth model. WeRise provides an optimum variety, and timing for sowing and fertilizer application which are key information to deal with yield increase in rainfed.', 2018, 'http://beta.werise.irri.org/', 1, 'Indonesia,Philippines', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=604&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 605, 'Field Calculator ', 'Field Calculator (FC) is a decision support tool for sustainable rice production. The Field Calculator is a web-based application for data collection and impact assessment of inputs and practices in rice production with the aim to provide insights and options for optimization towards sustainability. Functionality of FC: (1) collect day-to-day farmer rice crop management information and (2) calculate SRP standard scores and performance indicators for use by CORIGAP NARES partners and SRP adopters', 2018, 'not available yet', 1, NULL, 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=605&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 606, 'Rice Crop Manager', 'RCM is a decision-support tool designed for use by extension workers, crop advisors, agricultural service providers, and farmer leaders. It is accessible online on personal computers, tablets, or smartphones. ', 2018, '', 1, 'India,Philippines', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=606&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 607, 'Rice Doctor Odiya', 'Rice Doctor Odiya is a mid-season interactive diagnostic tool in local language that aims to help extension agents, students, researchers and farmers of Odisha to identify, have accurate and timely diagnosis or at least make a short list of more than 80 possible crop pest and disease problems that can occur in all growth stages of rice throughout the state of Odisha and provide actionable advice as recommendations on their management.', 2018, 'https://play.google.com/store/apps/details?id=com.lucidcentral.mobile.ricedoctor_oriya&hl=en_IN', 1, 'India', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=607&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 608, 'Sustainable Rice Platform (SRP) Standard and Performance Indicators ', 'SRP Performance Indicators are designed to assess sustainability improvements resulting from changes in farm practice, and include profitability, labor and land productivity, food safety, resource use efficiency, climate change mitigation, labor conditions, and women empowerment. The Standard defines a set of key requirements with different levels of performance allowing for a stepwise improvement and compliance process\r\n', 2018, 'http://www.sustainablerice.org/', 1, NULL, 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=608&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 609, 'AutoMonPH- a decision tool for system level water management using AWD principle', 'A decision tool for catalyzing the adoption of water-saving technologies by improving access to information, effective coordination among stakeholders, and transparency in water governance', 2018, 'http://ricetoday.irri.org/why-sustainable-water-management-needs-more-than-technologies/', 1, 'Philippines', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=609&phaseID=189');
