INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 612, 'Laser land leveling in Cambodia, Vietnam, Thailand', 'Precision land leveling using a stationary laser plane at the side of the field, a 4-wheel tractor pulled drag bucket equipped with a receiver, control box and hydraulics to keep the drag bucket at the same height with reference to the laser plane. Laser leveling allows field leveling with +/-1cm accuracy and leads to water savings (40-60%), increased yields 10-20%, better weed control and can also be used for field consolidation for improved mechanization.', 2018, 'http://ricecongress2018.irri.org/sites/default/files/Increasing%20economic%20and%20energy%20efficiency%20and%20reducing%20greenhouse%20gas%20emissions%20in%20rice%20production%20by%20applying%20laser-controlled%20land%20leveling.pdf', 1, 'Cambodia,Thailand,Vietnam', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=612&phaseID=189');
