INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 615, 'SeedCast mobile app', 'Innovative mobile app to estimate seed demand for rice varieties in Odisha, India. SeedCast will help estimate demand for varieties of rice seeds, which makes nearly 80% of the Kharif cultivation. This information made available to seed corporations, will help them produce the preferred seeds, in the required quantities, so gaps between supply and demand of rice seed varieties can be narrowed.  This will in turn encourage the replacement of older, lesser-yielding varieties of rice.', 2018, 'http://news.irri.org/2018/05/seedcast-innovative-mobile-app-launched.html', 1, 'India', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=615&phaseID=189');
