INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 620, 'Anaerobic digestion of rice straw, household batch system', 'Anaerobic digestion (AD), is a biological process that can degrade waste organic material by the concerted action of a wide range of microorganisms in the absence of oxygen. The process converts a large portion of rice straw into biogas, which is typically a mixture of\r\nmethane (60%) and carbon dioxide (40%). If captured, biogas can be utilized as a clean fuel for heat and power generation.', 2018, 'http://www.grisp.net/file_cabinet/files/942270/download/IRRI%20-%20Batch%20biogas%20using%20Superbag.pdf?m=1551764039', 1, 'Philippines', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=620&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 621, 'A novel combination of the \'adaptive research\' methodology with the Learning Alliance approach ', 'A novel combination of the \'adaptive research\' methodology with the Learning Alliance approach to bridge the gap between farmers and researchers, to expand the number of stakeholders with whom farmers interact., with the ultimate objective of scaling out solutions to improved farmers livelihoods.', 2018, 'https://www.sciencedirect.com/science/article/pii/S1573521417300052?via%3Dihub', 1, 'Myanmar (Burma)', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=621&phaseID=189');
