INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 623, 'Vocational training program for Agricultural Machinery Mechanics', 'Various institutions from the Government and development projects are providing technical innovations like rice seeders, fertilizer spreaders, land leveling equipment, mechanical weeding, and equipment for grain and seed cleaning but often lack capacity for integration and \'delivery\'. Value chain support services like after sales service provision or financing are only rudimentarily developed.   To increase Cambodiaâs innovation system capacity IRRI and Don Bosco in Battambang developed a vocational training program for Agricultural Machinery Mechanics.', 2018, 'http://www.grisp.net/file_cabinet/files/954865/download/Recent%20developments%20in%20supporting%20value%20chain%20support%20services%20for%20agricultural%20mechanization%20of%20rice%20based%20cropping%20systems%20in%20Cambodia.pdf?m=1560067969', 1, 'Cambodia', 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=623&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-15', 'Rice', 'AR', 2018, 'Yes', 624, 'IRRI Rice Quality Kit', 'The IRRI Rice Quality Assessment Kit consists of a set of various tools that help measuring one or several paddy, milled rice or seed quality traits. It is intended to be used in postharvest and other quality related training courses but several of the kits have already been purchased by millers and other value chain actors to help them in their daily tasks too', 2018, 'http://www.knowledgebank.irri.org/step-by-step-production/postharvest/milling/irri-rice-quality-assessment-kit', 1, NULL, 'https://marlo.cgiar.org/summaries/Rice/projectInnovationSummary.do?innovationID=624&phaseID=189');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 625, 'Evaluation of Common Application System for Anganwadi Workers in India', 'A mobile and web-based application that is designed to help the Anganwadi Workers deliver services effectively and efficiently.\r\nAt the supervisory level, it provides a combination of mobile and web-based applications and dashboards, making real time information available on service delivery and beneficiary nutrition status at the sector, block, district, state and national level. The goal is to improve management and facilitate early identification of gaps for informed decision making and timely actions.', 2018, 'https://www.dropbox.com/sh/5icrruj2w3qd3fu/AAD75UrlbMl3nHk1Ff68VMbfa?dl=0', 1, 'India', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=625&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 626, 'Measuring coverage of nutrition counselling interventions: Leveraging available data and program experiences', 'Data Notes focus on data visualization to highlight the geographic distribution and/or thematic issues related to nutrition in India. They draw on multiple sources of publicly available data.', 2018, 'http://poshan.ifpri.info/category/publications/data-notes/', 1, 'India', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=626&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 629, 'Launched a collection of outputs on how to design nutrition-sensitive value chain (NSVC) projects', 'Guides for project design, produced to fill a key knowledge gap in the emerging field of value chains for nutrition by providing guidance on how to design nutrition-sensitive value chain (NSVC) projects, with a particular focus on smallholder producers. This guide is relevant not only for IFAD but also for development actors in general, and for organizations working in agriculture and rural development in particular.', 2018, 'https://www.ifad.org/en/web/knowledge/publication/asset/40805038; https://www.ifad.org/web/knowledge/publication/asset/40861986; https://www.ifad.org/en/web/knowledge/publication/asset/40800472; https://www.ifad.org/en/web/knowledge/publication/asset/40271825; https://www.ifad.org/en/web/knowledge/publication/asset/40197427', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=629&phaseID=59');
