INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 635, 'Architecture for a tool matrix on methodologies for implementation to support Scaling up Nutrition (SUN) countries', 'Tool matrix aims to help address questions around: creating and/or strengthening global and national networks for implementation knowledge; knowledge brokering, which helps make scientific and contextual information more accessible to implementers and policy makers; creating country-specific tools to strengthen implementation of nutrition actions; and building capacity, support and funding for impactful research on implementation.', 2018, 'https://www.ifpri-faobangkokconference.org/side-events/', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=635&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 642, 'Evidence on how to leverage neglected and underutilized species (NUS) to improve nutrition ', 'Evidence summary to demonstrate how effective conservation and use of neglected and underutilized species (NUS) improve food and nutrition security with special attention to youths, adolescent girls and young mothers, strengthen adaptation to climate change and support empowerment of the poor and vulnerable groups to come out of poverty and social marginalization. ', 2018, 'https://www.dropbox.com/s/avdqprado2pe69v/NUS%20Nutrition%20IFAD%20feedback%20presentation%20final.pdf?dl=0', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=642&phaseID=59');
