INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 692, 'Module on the implementation of different livestock extension approaches in Tunisia', 'Four different extension methods were tested using the RCT (Randomized Control Trials) approach to test their impact on the adoption of agricultural innovations like new barley variety and feed blocks. The extension methods cover technical, organizational and economical training, sending of SMS, field visits and specific female empowerment activities.', 2018, 'https://dx.doi.org/20.500.11766/9379', 1, 'Tunisia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=692&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 695, 'Rights actualization model for a land tenure diagnostic to assess forest restoration opportunities', 'A rights actualization framework is proposed for strengthening tenure analyses in future assessments. Although developed in the context of forest landscape restoration, this approach should have broad utility for other land use initiatives where tenure rights and security are at stake.', 2018, 'https://www.sciencedirect.com/science/article/pii/S0264837718303855', 1, NULL, 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=695&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-24', 'WLE', 'AR', 2018, 'Yes', 696, 'A River Health Monitoring Framework for Myanmar: Methods and Tools', 'P518 developed a method for assessing river health (Myanmar Healthy Rivers Initiatives, MHRI). This method was applied to 2 rivers (Ayeyarwady and Thanlwin) to variables such as: flow stress, biodiversity indicators, water quality results based on magnitudes of different threats, and etc. ', 2018, 'https://drive.google.com/file/d/1bSieNUKlAbgDolsC69QkN3EJEcdIHd7U/view', 1, 'Myanmar (Burma)', 'https://marlo.cgiar.org/summaries/WLE/projectInnovationSummary.do?innovationID=696&phaseID=58');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 697, 'Redesigned soil health cards for Indian farmers', 'Indiaâs Ministry of Agriculture is implementing an $86 million Soil Health Card Scheme to provide nutrient recommendations to farmers based on local soil health tests. Soil health cards containing recommendations to improve management practices based on local soil measures are provided to farmers at two-year intervals. PIM helped re-design these cards to improve their effectiveness, and the new design is already used by Bihar Agricultural University.', 2018, '', 1, 'India', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=697&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-24', 'WLE', 'AR', 2018, 'Yes', 699, 'A Mobile Phone App that will provide more than half of Indian farmers with crop insurance in the next 2-3 years. ', 'Relying on satellite and climate data, combined with field data on crop yields, AgRISE delivers a crop health card, which enables insurers and government agencies to estimate crop damage and overall insured losses quickly and reliably for all of Indiaâs major crops. Based on the latest geospatial technology (Google Earth Engine and Open Data Kit for field data collection), the tool will strengthen the implementation of PMFBY, reducing costs and facilitating the whole process.', 2018, 'http://www.iwmi.cgiar.org/2018/09/press-release-mobile-phone-app-launched-to-strengthen-new-insurance-scheme-for-indias-farmers/', 1, 'India', 'https://marlo.cgiar.org/summaries/WLE/projectInnovationSummary.do?innovationID=699&phaseID=58');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 700, 'Inspire Challenge Award: Farm.ink: Analysing livestock social media data for farmer chatbot', 'Many posts on the topic of dairy farming & livestock can be found in farming Facebook groups in sub-Saharan Africa. These posts often contain detailed reports of livestock disease and queries and comments about productivity and management concerns but they remain unanalyzed. This project combines social media and ILRI data to create an open-source platform to analyze and visualize emerging livestock disease outbreaks in Kenya, and disseminate this information through Farm.ink\'s chatbot service.', 2018, 'https://bigdata.cgiar.org/inspire/inspire-challenge-2017/farm-ink-taps-into-facebook-data-to-help-livestock-farmers/', 1, 'Kenya', 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=700&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 701, 'Global Agricultural Data Innovation and Acceleration Network (GARDIAN)', 'GARDIAN provides easy access to thousands of data assets across CGIAR to enable new analyses and enhance innovation. It will facilitate data discovery from other relevant repositories, and also allow users to upload FAIR data. This will be enabled via a workflow and standalone tools for users to enter metadata, check for personally-identifiable information, and annotated datasets with ontologies. GARDIAN will also soon enable data visualization and mapping, with pipelines for data analysis and modeling. ', 2018, 'http://gardian.bigdata.cgiar.org', 1, NULL, 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=701&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 703, 'Agronomy Field Information Management System (AgroFIMS)', 'AgroFIMS, based on CIPâs Highly Interactive Data Analysis Platform (HiDAP) enables researchers to produce FAIR data without additional effort. AgroFIMS facilitates digital collection of semantically-annotated agronomic data, drawing fully on ontologies, particularly the Agronomy Ontology and the Crop Ontology. It allows data quality checking, statistical analysis, and generation of statistics reports, aligns a priori with CGIARâs CG Core metadata schema, and enables easy upload to the repository of choice.', 2018, 'https://apps.cipotato.org/hidapagrofimsv022/', 1, NULL, 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=703&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 704, 'Inspire Challenge Award: Seeing is believing â Using smartphone camera data ', 'Customized agricultural advice for specific crops based on local weather, soil data, pests and diseases, and input availability can improve management practices, productivity and profitability of smallholder agriculture. But personalized services do not incorporate potentially useful information on what farmers observe in their fields, such as crop color, texture and how the crop is growing. This project provides personalized agricultural advice based on localized information and visible crop characteristics derived from farmersâ own smartphone pictures.', 2018, 'https://bigdata.cgiar.org/inspire/inspire-challenge-2017/seeing-is-believing-using-smartphone-camera-data/', 1, 'India', 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=704&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 705, 'Inspire Challenge Award: Real-time diagnostics for devastating wheat rust', 'Wheat yellow rust, caused by Puccinia striiformis f. sp. tritici (PST), is currently considered the most damaging wheat disease globally, with yield losses sometimes higher than 60%. This project aims to develop and mainstream an affordable, mobile in-field pathogenomics platform called MARPLE (Mobile And Real-time PLant disEase) to revolutionize crop pathogen surveillance and diagnostics in real time. It will rely on the MinION mobile genome sequencer platform and assess its deployment in situ in Ethiopia.', 2018, 'https://bigdata.cgiar.org/inspire/inspire-challenge-2017/real-time-diagnostics-for-devastating-wheat-rust/', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=705&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 706, 'Inspire Challenge Award: Using IVR to connect farmers to market', 'The agriculture value chain in Nepal is informal and disaggregated, impeding the flow of information and resources. Farmers still rely heavily on farm gate sales, a series of middlemen, and have very little bargaining power. Farmers also face challenges finding services such as farm laborers and transportation to markets, impeding their ability to harvest and transport products to/from the market on time. Buyers face similar market barriers, constraining their operations to specific areas.', 2018, 'https://bigdata.cgiar.org/inspire/inspire-challenge-2017/using-ivr-to-connect-farmers-to-market/', 1, 'Nepal', 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=706&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 707, 'Inspire Challenge Award: Pest and disease monitoring by using artificial intelligence', 'This project aimed to increase the effectiveness of farm-level advice by leveraging three critical advances: 1) the democratization of AI thanks to open access platforms such as Googleâs TensorFlow, 2) the miniaturization of technology allowing affordable deployment, and 3) the development of massive communication and money exchange platforms such as M-Pesa that allow rural extension to scale as a viable economic model enabling last mile delivery in local languages.', 2018, 'https://bigdata.cgiar.org/inspire/inspire-challenge-2017/pest-and-disease-monitoring-by-using-artificial-intelligence/', 1, 'Kenya,United Republic of Tanzania', 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=707&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 708, 'Metadata Quality Checking Tool for Dataverse Repositories', 'An online visualization tool  that  support data managers to check the metadata quality of their center datasets on Dataverse. It is deployed in 5 centers: AfricaRice, CIP, CIAT, Bioversity and IFPRI', 2018, 'https://bioversity.github.io/dataverse-dashboard-curation/dataverse/dashboard.html', 1, NULL, 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=708&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('PTF-32', 'BigData', 'AR', 2018, 'Yes', 709, 'A data ontology is a machine- and -human readable logic and description of knowledge; the team expanded a general ontology for crop knowledge which can be used to connect CGIAR data with new applications, such as decision support systems.', 'The Agronomy Ontology (AgrO) is currently maintained on GitHub (https://github.com/AgriculturalSemantics/agro) and available through ontology registries. AgrO is key for ensuring the interoperability of agricultural data collected in the field. AgrO provides semantics to AgroFIMS, the agronomy field management system and therefore AgrO should cover all areas needed by AgroFIMS.', 2018, 'https://github.com/AgriculturalSemantics/agro', 1, NULL, 'https://marlo.cgiar.org/summaries/BigData/projectInnovationSummary.do?innovationID=709&phaseID=65');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 710, 'Interactive voice response advisory service for pig farmers', 'The innovation uses mobile phone technology (multiple phone types and service providers).', 2018, 'https://livelihoods-gender.ilri.org/2018/05/28/ilri-launches-mobile-phone-based-interactive-voice-advisory-service-for-pig-farmers-in-uganda/', 1, 'Uganda', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=710&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 711, 'Video-based agricultural extension - Integration of videos to create awareness among farmers', 'Research with Digital Green and the Ministry of Agriculture in Ethiopia has confirmed that the video-mediated approach is effective in increasing the uptake of the agricultural technologies and practices. Some field experiments are ongoing with implementation partners (e.g. in Cambodia and Uganda).', 2018, 'http://www.ifpri.org/blog/when-saying-see-it-works-isnt-enough-sharing-results-evaluation-video-mediated-extension', 1, 'Cambodia,Ethiopia,Uganda', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=711&phaseID=57');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-23', 'PIM', 'AR', 2018, 'Yes', 712, 'Updated social accounting matrices for Malawi and Rwanda', 'A social accounting matrix (SAM) is a database that represents the flows of all economic transactions that take place within an economy. SAMs are used to undertake computable general equilibrium modeling, which has informed the development of the National Agricultural Investment Plans of Malawi and Rwanda.', 2018, '', 1, 'Malawi,Rwanda', 'https://marlo.cgiar.org/summaries/PIM/projectInnovationSummary.do?innovationID=712&phaseID=57');
