INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 717, 'Food safety performance tool ', 'Tool developed to rank, compare and identify investment priorities for food safety in low- and middle-income countries (LMIC). The tool was developed to allow rapid, objective assessment of food safety system performance in LMIC. It explicitly follows a risk-based framework addressing risk assessment, risk management and risk communication. ', 2018, 'https://cgspace.cgiar.org/handle/10568/91963', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=717&phaseID=59');
