INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 746, 'Community based gastrointestinal parasite control in small ruminants in Ethiopia', 'Instead of advocating for farmer level training and parasite control, this approach tackles the problem at community level leading to quicker impact. We tested the method and produced a training module for vets/paravets on community based gastrointestinal parasite control in small ruminants, which they can use to train farmers.', 2018, 'https://www.dropbox.com/s/70bb5b6n5luv742/D5469%20GIT%20parasite%20intervention%20report.docx?dl=0', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=746&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 747, 'Post-mortem training module for vets in Ethiopia', 'Instead of relying on serology, this module highlights the advantages of conducting field post-mortem as a cost-effective diagnostic tool. ', 2018, 'https://cgspace.cgiar.org/handle/10568/100526', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=747&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 748, 'Training module for vets/paravets on coenurosis control in small ruminants in Ethiopia', 'Coenurosis is better controlled at community level and requires long-term commitment of farmers, vets and extension agents, thus a community approach was found better suited to combat this production disease with high impact.', 2018, 'https://cgspace.cgiar.org/handle/10568/100525', 1, 'Ethiopia', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=748&phaseID=60');
