INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 792, 'Chaya (native plant known as tree spinach) introduced in school feeding programme in Chiquimula, Guatemala', 'In 2017, the government of Guatemala approved a School Feeding Law to provide a healthier and more nutritious diet to students between 6 and 12 years of age in public schools. Bioversity International in coordination with Mancomunidad Copan Chorti proposed the native plant known as chaya as an ingredient for the school feeding program. Information provided included chaya\'s nutritional value, its ease of production, low cost, local availability and different recipes for cooking it.', 2018, 'https://drive.google.com/open?id=1z0_ARYDt_F-8UOmPnbOoZo8VRwQe4HuA5racwag5qHU', 1, 'Guatemala', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=792&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 793, 'Agriculture, Nutrition, and Health (ANH) Academy and ANH Academy Week Conference provide a unique, interdisciplinary conference and community to engage stakeholders around ANH', 'The Agriculture, Nutrition, and Health Academy Week fills a previously unmet need to convene people around agriculture, nutrition, and health (ANH) challenges. These researchers, practitioners and policymakers are becoming a collaborative global community of researchers and research users, with expertise and motivation to harness agriculture to reduce all forms of malnutrition and mitigate any negative health consequences. Dissemination of methods, metrics, and tools is accelerated through the ANH Academy community and annual conference. ', 2018, 'https://www.anh-academy.org/anh-academy-week; https://marlo.cgiar.org/projects/A4NH/study.do?expectedID=2775&edit=true&phaseID=59', 1, NULL, 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=793&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-21', 'A4NH', 'AR', 2018, 'Yes', 794, 'Ten Krishi Vigyan Kendras (agriculture extension offices) in two Indian States are setting up Farming System for Nutrition (FSN) models', 'The approach introduced agricultural remedies to the nutritional maladies prevailing in an area through mainstreaming nutritional criteria in the selection of the components of a farming system involving crops, nutrition garden of fruits and vegetables and animal husbandry components like poultry or fishery, accompanied by nutrition awareness.', 2018, 'https://icar.org.in/content/operationalising-farming-system-nutrition-through-kvks; http://lansasouthasia.org/blog/farming-system-nutrition-demonstration-models-maharashtra-%E2%80%93-thanks-lansa-%E2%80%98champion%E2%80%99; https://marlo.cgiar.org/projects/A4NH/study.do?expectedID=2771&edit=true&phaseID=59 ', 1, 'India', 'https://marlo.cgiar.org/summaries/A4NH/projectInnovationSummary.do?innovationID=794&phaseID=59');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-14', 'Maize', 'AR', 2018, 'Yes', 795, 'New Varieties adopted: Mayi Plus  II', 'CIMMYT maize yield trials have identified two varieties that out-yield anything grown in Haiti in both irrigated and rain-fed conditions.  Basically, CIMMYT has identified the best varieties for the next decade in Haiti.', 2018, 'not available', 1, 'Haiti', 'https://marlo.cgiar.org/summaries/Maize/projectInnovationSummary.do?innovationID=795&phaseID=63');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-13', 'Livestock', 'AR', 2018, 'Yes', 797, 'National guidelines for agri-nutrition community messaging in Kenya ', 'AVCD nutrition work has supported the development of new national agri-nutrition guidelines for community messaging in Kenya.  The Government of Kenya contributed and their logo is on the cards.', 2018, 'https://cgspace.cgiar.org/handle/10568/98538', 1, 'Kenya', 'https://marlo.cgiar.org/summaries/Livestock/projectInnovationSummary.do?innovationID=797&phaseID=60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-12', 'FTA', 'AR', 2018, 'Yes', 798, 'Sustainable Landscapes Rating Tool (SLRT) ', 'SLRT enables a rapid assessment of the key conditions for jurisdictional policies and governance that enable sustainable landscapes. The tool provides a snapshot of a jurisdictionâs capacity to establish and ensure effective functioning of policies, plans, strategies, regulations, monitoring systems and multi-stakeholder platforms, which, collectively, have been found to be important in supporting sustainable landscapes. Ratings (A, B, C) are given to various indicators that are validated by multiple stakeholders. Later information is made available to interested stakeholders (i.e. investors/donors), and allows such stakeholders to gauge the potential risk of investment in a state or region, through a relatively straightforward scorecard.', 2018, 'https://earthinnovation.org/state-of-jurisdictional-sustainability/', 1, 'Brazil,CÃ´te d,Ecuador,Indonesia,Mexico,Peru', 'https://marlo.cgiar.org/summaries/FTA/projectInnovationSummary.do?innovationID=798&phaseID=61');
