INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 890, 'Musa Germplasm Information System (MGIS) ', 'The Musa Germplasm Information System (MGIS) contains key information on Musa germplasm diversity, including passport data, botanical classification, morpho-taxonomic descriptors, molecular studies, plant photographs and GIS information\r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/4');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 891, 'Low-cost and energy-efficient small-scale flash dryer for cassava', 'Drying of cassava starch and flour is essential to ensure longer shelf life. Low-cost and energy-efficient pneumatic flash dryers represent a viable alternative to sun-drying for small processing units.\r\n', 2018, '', 1, 'Colombia,Ghana,Nigeria,Uganda,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/6');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 892, 'Cassava Peel TrackerÂ©', 'Cassava Peel TrackerÂ© is a web-app accessible from any web-enabled device (computer, mobile phone and tablet) that provides maps and information on fresh cassava peels availability: production sites, daily volumes processed, contacts. It supports decision making of and market linkages between farmers, cassava processors and animal feed producers.\r\n', 2018, 'http://seedtracker.org/peeltracker', 1, 'Nigeria', 'https://mel.cgiar.org/innovation/getinnovationview/id/8');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 893, 'High quality cassava peel for animal feed', 'Technical formulation of high quality cassava peel mash for animal feed.\r\n', 2018, '', 1, 'Nigeria,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/9');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 894, 'Transgenic potato variety resistant  to late blight', 'Potato variety Victoria, which is a popular variety in Uganda, has been transformed with three stacked genes taken from wild potato species, that confer resistance to the late blight pathogen, Phytophtora infestans.  Having more than one resistance gene ensures the resistance is more robust, and cannot be overcome easily by the pathogen.  This product has been tested in multi-location trials under confined conditions, and shown to be identical to the variety Victoria, and highly resistant to late blight.', 2018, 'https://doi.org/10.1111/pbi.13042', 1, 'Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/10');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 895, 'Transgenic banana resistant to banana Xanthomonas wilt (BXW) disease ', 'Banana Xanthomonas wilt (BXW)  is a serious threat to banana production in East Africa. Transgenic bananas have been generated by inserting Hrap or Pflp genes in two local banana cultivars, and shown to be resistant to BXW over five seasons in confined field trials.\r\n', 2018, 'https://doi.org/10.1002/fes3.101', 1, 'Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/11');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 896, 'Global information system for in situ conservation of RTB crops', 'The aim of this system is to allow systematic monitoring of in situ diversity of RTB crops (Bananas, Cassava, Potatoes and Yams) including landraces crop wild relatives.  This will allow to follow effects on biodiversity in response to climate, social or economic changes..\r\n', 2018, 'https://cgspace.cgiar.org/handle/10568/97657,https://dx.doi.org/10.1007/s10722-018-0690-x', 1, 'Papua New Guinea,Peru', 'https://mel.cgiar.org/innovation/getinnovationview/id/27');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 897, 'Population Hybrid Breeding in Sweetpotato', 'Populations are separated into distinct heterotic groups, and selections for best parents done for each group separately (reciprocal recurrent selection cycle).  The best parents of each heterotic group, are then crossed to the best of the other group (\"elite crosses\").  This has resulted in dramatic genetic gains (due to heterosis) for the traits for which the parents are selected, such as storage root yields, iron content in the storage roots, and reduced sweet taste.\r\n', 2018, 'https://dx.doi.org/ 10.1515/opag-2017-0014', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/49');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 898, 'Orange-fleshed Sweetpotato PurÃ©e for Bakery Applications in Kenya', 'Techniques for production and storage of fresh sweetpotato puree and/or concentrate for use with limited or no cold chain, including chemical preservatives and vacuum packing.\r\n', 2018, 'https://dx.doi.org/ 10.1515/opag-2017-0014', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/60');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 899, 'Participatory Market Chain Approach (PMCA)', 'This multi-stakeholder approach is based on a flexible three-stage participatory process to improve communication, build trust, and facilitate collaboration among value chain stakeholders so that they can jointly identify, analyze, and exploit new market opportunities.\r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/61');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 900, 'Marker-based assays for extreme resistance to Potato Virus Y gene Ryang.  One is a multiplex assay and the other is for determining the number of resistant alleles in tetraploid potato.', 'Two DNA marker assays developed to accelerate the introgression of Ryadg gene into breeding lines by marker-assisted selection. First, a multiplex assay for RYSC3, M6 and M45 markers flanking the Ryadg gene (so, all markers assayed in same reaction tube) and validated on 6,521 individuals. Second,  an allele-dosage assay to identify multiple copies of the Ryadg resistant allele (so can determine how many copies there are of the resistant allele, as potato has a tetraploid genome - 4 chromosomes). \r\n', 2018, 'https://doi.org/10.1007/s00122-018-3123-5', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/62');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 901, 'Insect Life Cycle Modeling software (ILCYM) ', 'Insect Life Cycle Modeling software (ILCYM - version 4.0) facilitates the development of pest insect phenology models and provides analytical tools for studying pest population ecology. \r\n\r\n', 2018, 'https://research.cip.cgiar.org/confluence/display/ilcym/Home', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/66');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 902, 'AdiosMacho-PoÂ® and AdiosMacho-StÂ®: innovative biorational products to control potato pests', 'Biorational products based on attract-and-kill approach for effective field and storage control of P. opercullela (Po) and S. tangolias (St) in potato.\r\n', 2018, 'http://www.bjnrd.org/uploads/pdf/1480564528.pdf', 1, 'Australia,Bhutan,Bolivia,Ecuador,Nepal,Peru', 'https://mel.cgiar.org/innovation/getinnovationview/id/67');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 903, 'Farmer Business school', 'Farmer Business school is a participatory action learning approach to support farmer groupsâ participation to and benefit from agricultural value chains guided by a market-chain curricular framework, through a series of learning activities over a production-marketing cycle, including interaction with other chain actors and stakeholders. It is a modular/ step-wise platform for developing or upgrading value chains especially fit for small farmer-entrepreneurs. \r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/68');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 904, 'Scaling readiness approach - A science-based approach to develop scaling strategies', 'The Scaling Readiness Approach is an innovation that analyze the readiness for scaling of a specific innovation package.  It assesses the readiness of technological and non-technological innovations independently and as packages to determine their readiness for scaling in a specific context.  Its use can help identify key barriers for scaling and inform the development of scaling strategies better adapted to a specific context.\r\n', 2018, 'http://ar17.iita.org/science-based-delivery-to-transform-african-agriculture/', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/73');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 905, 'Guidelines for innovation platforms in agricultural research for development: decision support fon how to design, budget and implement impactfull innovation', 'This is a decision support tool on how to design, budget and implement impactful innovation platforms.  It reflects on when and under what conditions innovation platforms are appropriate mechanisms to foster collective action and innovation. It provides information on key design and implementation principles, the financial and human resources that need to be made available, and makes suggestions for more effective monitoring, evaluation and learning. \r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/74');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 906, 'Prototype ICT4BXW app', 'It is a prototype app to combat banana diseases through digital innovation.  The app registers farmer users, locates them geographically and provides support for diagnosis, management, and implementation of agronomic practices for banana farming.\r\n', 2018, 'https://dx.doi.org/10.1016/j.njas.2018.07.008', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/75');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 907, 'VirusDetect: high-throughput sequencing of small RNAs for virus detection', 'VirusDetect is a bioinformatics pipeline that efficiently analyzes large scale small RNA (sRNA) datasets for both known and novel virus identification. The technology has proven to be highly efficient, sensible and reliable in plant and animal virus detection.\r\n', 2018, 'http://dx.doi.org/10.1016/j.virol.2016.10.017', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/77');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 908, 'Partial root-zone drying (PRD) irrigation technique to increase water use efficiency in potato cropping systems', 'Partial root-zone drying consists in irrigating a portion of the root system of the plant while the other portion is left to dry. This irrigation technique allows for water saving, increases water use efficiency and has no negative effects on tuber yield. \r\n', 2018, 'https://dx.doi.org/10.1007/s11540-018-9393-0', 1, 'China,Ethiopia', 'https://mel.cgiar.org/innovation/getinnovationview/id/78');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 909, 'Management practice based on Single Diseased Stem Removal (SDSR) for quick and effective banana recovery in Xanthomonas wilt (XW) affected regions', 'Single diseased stem removal is a disease management practice involving cutting a plant showing Xanthomonas wilt symptoms at soil level, sterilizing cutting tools using fire, and early male bud removal using a forked stick. It\'s very effective in reducing the incidence of the disease and easy to apply. It requires less labour than alternative practices.\r\n', 2018, '', 1, 'Burundi,Democratic Republic of the Congo,Rwanda,Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/80');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 910, 'Pestdisplace: platform to integrate and collectively monitor the occurrence and movement of pests and diseases in RTB and other crops', 'Pestdisplace uses novel visualization tools to share, track and communicate the distribution of crop pests and diseases over time and space (longituinal & spatial). A great part of this data was passive (stored in old reports and lab-books) and could not be used for decision making. By facilitating data collation from research and official sources the platforms is intended to accelerate decision making by stakeholders thus reducing risk and/or the impact of pests and diseases on crop yield. \r\n', 2018, 'https://pestdisplace.org/', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/86');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 911, 'A framework for priority-setting in climate smart agriculture (CSA) research', 'A flexible framework for assessing and comparing different interventions and investments that addresses the key elements of CSA. The framework describes some features of CSA that make prioritisation a challenge and proposes a framework for prioritization based on a brief review of existing tools and methods for priority setting in agriculture and some case-study examples. \r\n', 2018, 'https://dx.doi.org/10.1016/j.agsy.2018.09.009', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/88');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 912, 'High-Throughput Yam Anthracnose Phenotyping Using Detached Leaf Assay and Digital Imaging', 'Conventional field phenotyping is unreliable as infection rate is influenced by weather, and the disease severity assessed using a 1 to 5 rating scale is subjected to âraterâ bias. Therefore, a new phenotyping method was developed by combining in vitro detached leaf assay (DLA) and accurate measurement of YAD symptomatic area using the âLeaf Doctorâ App (Pethybridge & Nelson, Plant Dis 99, 1310, 2015). \r\n', 2018, 'https://doi.org/10.13140/RG.2.2.32630.65605', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/87');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 913, 'Multi-stakeholder framework for intervening in RTB seed systems', 'The tool is designed to better understand RTB seed systems or to improve seed system interventions (e.g., projects or programs) It helps in identifying key stakeholders and in assessing availability, access, and quality of seed as well as in  identifying bottlenecks and in designing key actions for the upcoming intervention.\r\n', 2018, '', 1, 'Ecuador,Ethiopia,Ghana,Kenya,Malawi,Nigeria,Peru,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/89');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 914, 'Seed Tracker: online application for real-time tracking of cassava seed production that supports communication and networking of cassava producers', 'The Cassava Seed Trackerâ¢ is a fully featured program for real-time tracking of cassava seed production, including pre-planting planning, registration of seed fields, crop management, harvesting, quality assessment and quality assertion. The Cassava Seed Tracker is also a digital platform for communication and networking of cassava seed producers and service providers for common good.\r\n', 2018, 'http://seedtracker.org/', 1, 'Brazil,Ghana,Nigeria,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/90');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 915, 'Impact network analysis (INA) to understand and describe RTB seed systems', 'Impact Network Analysis (INA) is a multilayer network analysis that looks at the effects of a technology â such as a new variety, a management strategy, or other types of information â as it influences the outcomes of linked socioeconomic networks and biophysical networks. \r\n', 2018, '', 1, 'Cambodia,Ecuador,Ethiopia,Georgia,Kenya,Uganda,Vietnam', 'https://mel.cgiar.org/innovation/getinnovationview/id/91');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 916, 'Integrated seed health approach to help decision-making on RTB seed management', 'This model provides estimates of seed degeneration risk at a location, as a function of factors such as weather conduciveness, crop disease resistance, and success rates for interventions such as positive selection and roguing.  It also provides estimates of optimal timing for purchase of quality-declared seed to maintain yield within acceptable bounds.\r\n', 2018, 'https://dx.doi.org/10.1111/ppa.12958', 1, 'Ecuador,Georgia,Kenya,Nigeria,Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/92');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 917, 'Positive selection to improve on-farm seed potato management', 'Positive selection (or âSelect the bestâ) is an efficient and simple technique for improving on-farm seed potato management. The best plants (robust and not showing symptoms of key diseases) are marked before and after flowering. These plants are harvested and only those with good yield are selected to provide tubers for the next growing season. This technique is complemented with integrated pest and disease management, and postharvest techniques.\r\n', 2018, '', 1, 'Bolivia,Ecuador,Ethiopia,Kenya,Malawi,Peru,Rwanda,Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/93');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 918, 'Method based on a combination of tissue culture, chemotherapy and thermotherapy to produce  cassava plants clean from viral infections ', 'Using a combination of tissue culture, chemotherapy and thermotherapy and after 3 cycles of cleaning it was possible to obtain virus-free planting material for 31 varieties of five countries (Kenya, Malawi, Mozambique, Tanzania and Uganda). The cleaning process was performed in United Kingdom, this first cross-boundary effort provides important lessons for mitigating the two-major cassava viral diseases in Africa.\r\n', 2018, 'https://dx.doi.org/10.1016/j.pmpp.2018.09.002', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/94');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 919, 'Landscape IMAGES modeling framework for multi scale spatially explicit analysis of tradeoffs and synergies among ecosystem services provisioning across agricultural landscapes', 'The LandscapeIMAGES is a modeling framework for multi-scale spatially explicit analysis of trade-offs and synergies among ecosystem services provisioning across agricultural landscapes. It generates large sets of spatially explicit land-use and management scenarios to inform discussions among stakeholders involved in landscape planning processes. \r\n', 2018, 'https://dx.doi.org/10.1016/j.landurbplan.2017.12.008', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/95');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 920, 'Rapid multiplication of planting material through rooted apical cuttings for seed potato systems', 'Rapid multiplication technique for potato planting material. It consists in producing rooted cuttings from tissue culture plantlets grown in screenhouses. Once planted in the field, each cutting produces 7 to 10, and up to 15+, tubers which are multiplied a further season or two, then the harvest is sold as seed. \r\n', 2018, '', 1, 'Kenya', 'https://mel.cgiar.org/innovation/getinnovationview/id/102');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 921, 'Varieties for mid altitude, humid subtropical agro-ecologies in Sub-Saharan Africa (SSA), focusing on heat tolerance, late blight and virus resistance - Climate smart mid altitude potato', 'CIP has developed a new population of late blight resistant and heat tolerant potatoes designated LBHT.  These clones have been sent to several countries in Africa, Asia and Latin America for variety development and use in breeding. \r\n', 2018, '', 8, 'Cameroon,Ethiopia,Kenya,Nigeria,Rwanda,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/103');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 922, 'Direct RT-RPA : a robust, accurate, sensitive and quick method for detection of potyviruses from recalcitrant plant species based on reverse transcription-recombinase polymerase amplification (RT-RPA)', 'Potyviruses (genus Potyvirus; family Potyviridae) are widely distributed and represent one of the most economically important genera of plant viruses. The developed method detects each target virus directly from plant leaf extracts prepared with a simple and inexpensive extraction method avoiding laborious extraction of high-quality RNA. This assay coupled with direct detection make it a suitable for the detection of viruses in seed yams as well as plants. \r\n', 2018, 'https://dx.doi.org/10.1016/j.ab.2018.01.019', 1, 'Nigeria', 'https://mel.cgiar.org/innovation/getinnovationview/id/106');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 923, 'High-yielding and late blight resistant potato clones ready to be released as new varieties or as parents in crossing plans', 'Starting in 2011, 30,000 genotypes have been assessed to select those with high yields and good agronomic and commercial characters such as skin color, flesh color, tuber shape and eyes depth, plant vigor, uniformity of tubers. After seven years of evaluation and selection, 18 clones that showed phenotypic stability for selected traits have been identified.\r\n', 2018, 'https://research.cip.cgiar.org/cipcatlg_ac/index.php?language=1&name=English', 70, 'Peru', 'https://mel.cgiar.org/innovation/getinnovationview/id/119');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 924, 'Two potato varieties biofortified with iron and zinc, resistant to late blight and PVY virus adapted for tropical highland and mid elevation ecologies', 'A breeding strategy initiated in 2012 resulted in new tetraploid cultivars with resistance to the most important diseases such as late blight and viruses and tolerance to abiotic stresses (heat and drought) while maintaining the nutritional quality of the tuber with high levels of iron and zinc.\r\n', 2018, '', 2, 'Bhutan,Ethiopia,India,Kenya,Peru,Rwanda', 'https://mel.cgiar.org/innovation/getinnovationview/id/120');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 925, 'Ground penetrating radar for estimating root bulking rate in cassava ', 'The ability to image and quantify subsurface root structure would allow breeders to classify root traits for rapid selection and allow agronomists  to derive effective cultivation practices, without using destructive root harvesting approaches. The capability of ground penetrating radar (GPR) to predict root-bulking rates (formation of cassava storage roots during the growth season) was shown.  Using current modeling methods,one can estimate root bulking rates for selection of early maturing cassava varieties.\r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/121');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 926, 'High-yielding and black Sigatoka resistant banana hybrids (NABIO ) for East Africa', 'New banana genotypes developed by the Uganda National Agricultural Research Organization in collaboration with Bioversity International.\r\n', 2018, 'https://dx.doi.org/10.5897/JPBCS2018.0720', 2, 'Uganda', 'https://mel.cgiar.org/innovation/getinnovationview/id/122');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 927, 'Framework for multidimensional analysis of sustainability of intensification options in RTB-based systems', 'The framework proposes transparent and well described procedures to select sustainability indicators to assess and monitor progress in SI of agricultural systems \r\n', 2018, '', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/125');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 928, 'Qualitative data collection tool for gender analysis to understand local perceptions of agency and decision making', 'The ladder of power and freedom is a qualitative data collection tool to understand local perceptions of agency and decision making.\r\n', 2018, 'http://42q77i2rw7d03mfrrd11pvzz.wpengine.netdna-cdn.com/wp-content/uploads/2018/04/Ladder-of-Power-and-Freedom-24April2018.pdf', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/129');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 929, 'Gender checklist for introducing new Root and Tuber Crops technologies to men and women', 'This checklist is intended to help ensure that gender is adequately addressed in  workplans and interventions. Considering gender dimensions of farming activities helps us to identify context-specific social factors that support/hinder uptake of new technologies. The checklist has two sections; understanding gendered interests and preferences; and understanding gendered decision-making power. \r\n', 2018, 'https://hdl.handle.net/10568/89461', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/130');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 930, 'Gender-responsive participatory videos: a guide for facilitators', 'Participatory-video is a research technique in which farmers film and publicly screen interviews with their peers. It is a powerful tool to build relationships between researchers and farmers and collect the real voices of the different social groups in the community. This guide has been prepared for researchers and development practitioners who are interested in applying this technique.\r\n', 2018, 'https://cgspace.cgiar.org/handle/10568/92884', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/131');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 931, 'Gender in Agricultural Mechanization: Key guiding questions.', 'The tool presents guiding questions to stimulate researchers to think about the gender implications of actual or possible interventions involving machinery or that may depend on existing machinery. \r\n', 2018, 'http://gender.cgiar.org/?s=Gender+in+Agricultural+Mechanization&s_area=all', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/132');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-16', 'RTB', 'AR', 2018, 'Yes', 932, 'The Banana Breeding Tracking System (BTracT) ', 'The system tracks all stages in breeding, beginning with the automatic decision making of crosses in the field based on flowering plants, followed by all stages of hybrid production (from seed to embryo culture to nursery). Plants are barcoded and the data management system implemented and integrated directly with MusaBase.  In this manner it is possible to monitor progress of crosses by pollination, thru seed set and eventual distribution, all captured in a database.\r\n', 2018, 'http://breedingbetterbananas.org/wp-content/uploads/2018/05/Posters-BBB-meeting-2018-selected.pdf', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/133');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-18', 'GLDC', 'AR', 2018, 'Yes', 933, 'Multi-model systems analysis to identify Low Emissions Development Pathways â exploring synergies and trade-offs in Mahbubnagar District, Telangana, India', 'Triple-win solutions with positive contributions to productivity, adaptation and mitigation are the key to achieve sustainable agricultural systems. Quantifying synergies and trade-offs among these we employed simulation modelling- crop, livestock and whole-farm models, and Cool Farm Tool to estimate net GHG emissions. Multi-model systems analysis using participatory design and tools could be a decision support for policymakers guiding investments towards synergistic climate smart agriculture strategies.', 2018, 'http://oar.icrisat.org/11038/', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/96');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 935, 'A co-management strategy for Lake Fisheries', 'Lake fisheries provide an important source of micro nutrients, animal protein and livelihoods.  Lake Nasser in Upper Egypt provides a case of a critical yet under performing fishery i.e., stocks are overfished and catches are decreasing. Building on earlier recommendations, WorldFish  conducted a further assessment in 2017 to design and formalize an ecosystem-based, co-management approach to manage and rebuild the fishery (including using restocking techniques). \r\n', 2018, 'http://hdl.handle.net/20.500.12348/1885', 1, 'Egypt', 'https://mel.cgiar.org/innovation/getinnovationview/id/137 ');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 936, 'Co-management of community fish refuges (CFRs) to enhance fish production, water security and adaptive capacity to climate change', 'A CFR is a form of stock enhancement or a fish conservation measure to improve the productivity of rice-field fisheries. The CFR is a refuge, or sanctuary, for brood fish that is protected from fishing year-round and is especially important as fish habitat during the dry season in seasonally inundated rice fields. During the dry season fish seek deeper pools that become disconnected, during the flood season the fish emerge from the refuge ponds to spawn and feed throughout the rice field', 2018, 'http://hdl.handle.net/20.500.12348/3319', 1, 'Cambodia', 'https://mel.cgiar.org/innovation/getinnovationview/id/139');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 937, 'Hilsa fisheries co-management and livelihood buffering strategies', 'Co-management strategies (an innovative combination of local social innovations, womenâs community savingâs groups, area management strategies and national policy adjustments) were applied to restore the hilsa shad fishery.  In 2018 research outputs illustrate the process and outcomes including substantial increases in hilsa landings.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/2669', 1, 'Bangladesh', 'https://mel.cgiar.org/innovation/getinnovationview/id/142');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 938, 'A framework for assessing and building adaptive capacity to climate change in small-scale fisheries communities', 'We developed an analytical framework comprised of five dimensions that allows analysts to identify obstacles and options for building capacities to adapt and to innovate. The research applies and examines the framework in coastal fishing communities.   The \'agency\' dimension of the framework is informed by earlier WorldFish-led research on gender and agency.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/667', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/143');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 939, 'Solar-social innovations to reduce waste and loss, increase incomes and improve equity in capture fisheries systems', 'The use of solar technology offers opportunities to reduce value and quality losses experienced in fisheries value chains. FISH research illustrates when solar-based initiatives are developed through action research and combined with social innovations (particularly gender transformative or gender sensitive approaches) complementary outcomes ranging from increased gender equity, reduction of fish quality loss and increased likelihood of sustainability.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/676', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/144');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 940, 'Design of tilapia single nucleotide polymorphisms (SNP) chip', 'A SNP chip optimized for genomic selection of GIFT and Abbassa tilapia strains was designed in collaboration with the Roslin institute for manufacture and application to Nile Tilapia genetic improvement programs in 2019.\r\n', 2018, 'https://cgiar.sharepoint.com/:b:/s/FISHOperations/EfDEX6GU5UtGiX68KDaoTFgB50LFclZa5tlLMlAnPjt1uA?e=XviNEU', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/146');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 941, 'Generation 17 GIFT Nile tilapia strain', 'Generation 17 of the GIFT tilapia strain was spawned, grown out and assessed (168 families).  \r\n', 2018, 'https://cgiar.sharepoint.com/:b:/s/FISHOperations/ETBZ0QkyWPJOjZtkHU8kfb0B1S_8vUVCty_YKHcPLvPhVg?e=qnpK4K', 17, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/147');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 942, 'Generation 15 of the Abbassa Nile Tilapia strain ', 'Generation 15 of the Abbassa tilapia strain was produced âspawned and grown out (125 families).\r\n', 2018, 'https://cgiar.sharepoint.com/:b:/s/FISHOperations/EYRkc7aJZBdNlkG73c-j7C8BbR44YysI47hmXf2D7KniHQ?e=ZDSR9f', 15, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/148');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 943, 'Generation 2 of Rohu Carp', 'Generation 2 of Rohu Carp was produced â grown out and assessed (240 families)\r\n', 2018, 'http://blog.worldfishcenter.org/2016/12/carps-genetic-improvement-programs-and-technology-platform-in-bangladesh/', 2, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/149');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 944, 'DArTseq genetic marker developed for Catla', 'Analysis of DArTseq genetic marker data successfully identified and characterized single nucleotide polymorphisms (SNPs) and silicoDArT markers in catla. The analysis identified an unexpectedly high level of sibship among breeding population founders and found no or low levels of divergence in SNPs among the three river populations studied.  \r\n', 2018, 'https://doi.org/10.7910/DVN/6LEU9O ', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/150');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 945, 'A way to breed for a new trait of robustness in GIFT identified', 'The analysis revealed the presence of genetic heterogeneity of both body weight and its environmental variance in all environments. The presence of genetic variation in residual variance of body weight implies that the residual variance can be modified by selection. Incorporating both genotype-by-environment interaction and micro-environmental sensitivity information may help in selecting robust genotypes with high performance across environments and resilience to environmental fluctuations.  \r\n', 2018, 'http://hdl.handle.net/20.500.12348/703', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/152');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 946, 'A way to breed for feed efficiency in GIFT', 'Video assessment of feed intake on individual fish reared in groups was used to estimate the genetic parameters of six growth traits, feed intake, feed conversion ratio (FCR) and residual feed intake in 40 pedigreed families of the GIFT strain of Nile tilapia.  The FCR response to different selection criteria were estimated to assess the potential of genetics as a means of increasing FCR in tilapia.  \r\n', 2018, 'https://doi.org/10.1186/s12863-018-0691-y', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/153');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 947, 'A smartphone app providing aquaculture extension Information', 'A virtual aquaculture extension application has been co-designed with a local ICT company called Greenway. The app has been designed based on their previous agriculture and livestock App in use by 85,000 farmers. The Aquaculture App brings access to weather forecasts for the immediate area where the farmer is located. It also delivers messages about sourcing inputs (seed and feed), market prices and trends plus climate risks.\r\n', 2018, 'http://www.mmgreenovator.com/', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/154');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 948, 'The use of water storage ponds and homestead irrigation channels for fish production in Myanmar', 'Irrigation canals are a relatively under-used water body for the culture of fish in Myanmar compared to other countries in the region. The homestead irrigation channels, called âchan myaungâ in Myanmar, have been successfully explored as a viable option for fish culture in specific district in Myanmar.  This has demonstrated that those water bodies can be used as productive systems for the culture of finfish. Potentially, this has important effect of poverty reduction and food and nutrition security', 2018, 'https://digitalarchive.worldfishcenter.org/handle/20.500.12348/3336', 1, 'Myanmar (Burma)', 'https://mel.cgiar.org/innovation/getinnovationview/id/155');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 949, 'Methods for improving productivity of Aquatic Agricultural systems in Cambodia ', 'Methods range from inclusion of Small Indigenous fish Species (SIS) in polyculture, home gardening production systems, to end of rainy season rice field ponds. Production aimed at providing additional incomes and supply nutrient rich and diversified food to households, targeting children and women of reproductive age.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/3323', 1, 'Cambodia', 'https://mel.cgiar.org/innovation/getinnovationview/id/156');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 950, 'Stimulation of natural foods production in ponds by use of low protein feeds with fibre (NSP) rich carbohydrates in Tilapia culture', 'Dietary requirements for fish are normally determined in absence of natural food, while the majority of fish (incl. tilapia) are cultured in ponds, in which the natural food also contributes to fish growth. The innovation revealed that low protein feeds with fibre (NSP) rich carbohydrates provide both the necessary nutrients for the fish and, via the faeces, stimulate the natural food production in the pond that also contributes to the fish growth. \r\n', 2018, 'http://hdl.handle.net/20.500.12348/1184', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/156');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 951, 'Tools to assist farmers with low or no numeracy skills to manage their aquaculture pond', 'Two tools involved: Secchi disk to measure water transparency in fishponds and determine the amount of plankton; Tool to infer fish weight from their length. This consists in a laminated colour coded ruler to measure length of the fish including a guide to interpret the measured length in cm into weight in grams.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/3334', 1, 'Sierra Leone', 'https://mel.cgiar.org/innovation/getinnovationview/id/158');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 952, 'Better Management Practices for Carp Intensification by Women Self Help Groups ', 'Women Self Help Groups are trained in bespoke Better Management Practices for carp farming with small indigenous fish species (Mola).\r\n', 2018, '', 1, 'India', 'https://mel.cgiar.org/innovation/getinnovationview/id/159');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 953, 'GIFT Tilapia introduction in Odisha State India', 'Production and management system set up for starting the production of GIFT on a larger scale in Odisha State.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/3321', 1, 'India', 'https://mel.cgiar.org/innovation/getinnovationview/id/160');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-11', 'Fish', 'AR', 2018, 'Yes', 954, 'Low-cost feed formulation with local ingredients in DR Congo (Bukavu and Kinshasa)', 'Following a feed ingredient scoping in Bukavu and Kinshasa, cost-effective tiapia feed was formulated using locally available ingredients.\r\n', 2018, 'http://hdl.handle.net/20.500.12348/3335', 1, 'Democratic Republic of the Congo', 'https://mel.cgiar.org/innovation/getinnovationview/id/161');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-18', 'GLDC', 'AR', 2018, 'Yes', 955, 'The Crop Network Group (CNG) as a platform for crop Product design, development, testing, advancement and delivery in Africa.', 'The Crop Network Group (CNG) is a crop improvement network of multi-disciplinary teams from NARS, CG, Advanced Research Institutes (ARIs), NGOs, private seed sector and processing sector. CNG is a platform for product design, strategy, development, testing, advancement and delivery. The CNG will also be a knowledge-sharing platform, facilitating continuous capacity building. The CNG team will work closely with Excellence in Breeding (EiB) platform.', 2018, 'http://gldc.cgiar.org/optima-soy-africa-launched-to-scale-up-production-and-commercialization-of-industrial-soybean-varieties/', 1, NULL, 'https://mel.cgiar.org/innovation/getinnovationview/id/104');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-18', 'GLDC', 'AR', 2018, 'Yes', 956, 'Introgression of high oleic trait in groundnut.', 'High oleic groundnut is preferred by food industry for shelf-life benefits, and consumer health benefits. Genotyping for selection of FAD2A and FAD2B mutant alleles, and robust-phenotyping using Near Infrared Reflectance Spectroscopy (NIRS) enabled introgression of âhigh oleicâ trait in Spanish and Virginia Bunch backgrounds. The fast track development is possible through reduced generation interval using low cost methods, and early generation and multi-location testing data enabled line advancement decisions.', 2018, 'http://oar.icrisat.org/10923/', 1, 'Australia,Bangladesh,Ethiopia,India,Malawi,Mali,Myanmar (Burma),Uganda,United Republic of Tanzania', 'https://mel.cgiar.org/innovation/getinnovationview/id/162');
INSERT INTO `qa_policies_view_table` (`crp_id`, `crp`, `phase_name`, `phase_year`, `included_AR`, `project_innovation_id`, `title`, `narrative`, `year`, `evidence_link`, `number_of_innovations`, `countries`, `pdf`) VALUES ('CRP-18', 'GLDC', 'AR', 2018, 'Yes', 957, 'Groundnut cultivar: ICGV 07222 (GJG 32)', 'Drought tolerant, resistant to rust and moderately tolerant to Peanut bud necrosis disease with medium bold (100 seed mass of 38 g), 87% sound mature kernels, tan seed coat for Zone III b for post-rainy irrigated cultivation (rabi-summer) for multiple states in India.', 2018, 'http://grainlegumes.cgiar.org/improved-groundnut-variety-released-in-india/', 1, 'Ethiopia,India', 'https://mel.cgiar.org/innovation/getinnovationview/id/164');
